// SPDX-License-Identifier: agpl-3.0
pragma solidity 0.7.6;
pragma experimental ABIEncoderV2;

import "../interfaces/IBaseOracle.sol";
import "../interfaces/IPoolHelper.sol";
import "../interfaces/IChainlinkAggregator.sol";
import "../interfaces/IEligibilityDataProvider.sol";
import "../dependencies/openzeppelin/contracts/SafeMath.sol";
import "../dependencies/openzeppelin/upgradeability/Initializable.sol";
import "../dependencies/openzeppelin/upgradeability/OwnableUpgradeable.sol";

/// @title PriceProvider Contract
/// @author Radiant
/// @dev All function calls are currently implemented without side effects
contract PriceProvider is Initializable, OwnableUpgradeable {
    using SafeMath for uint256;

    /// @notice Chainlink aggregator for USD price of base token
    IChainlinkAggregator public baseTokenPriceInUsdProxyAggregator;

    /// @notice Pool helper contract - Uniswap/Balancer
    IPoolHelper public poolHelper;

    /// @notice Eligibility data provider contract
    IEligibilityDataProvider public eligibilityProvider;

    /// @notice Base oracle contract
    IBaseOracle public oracle;

    /**
     * @notice Initializer
     * @param _baseTokenPriceInUsdProxyAggregator Chainlink aggregator for USD price of base token
     * @param _poolHelper Pool helper contract - Uniswap/Balancer
     * @param _oracle Base oracle contract
     */
    function initialize(
        IChainlinkAggregator _baseTokenPriceInUsdProxyAggregator,
        IPoolHelper _poolHelper,
        IBaseOracle _oracle
    ) public initializer {
        require(address(_baseTokenPriceInUsdProxyAggregator) != (address(0)), "Not a valid address");
        require(address(_poolHelper) != (address(0)), "Not a valid address");
        require(address(_oracle) != (address(0)), "Not a valid address");
        __Ownable_init();

        poolHelper = _poolHelper;
        baseTokenPriceInUsdProxyAggregator = _baseTokenPriceInUsdProxyAggregator;
        oracle = _oracle;

        update();
    }

    /**
     * @notice Update oracles.
     */
    function update() public {
        if(oracle.canUpdate()) {
            oracle.update();
        }
    }

    /**
     * @notice Returns the latest price in eth.
     */
    function getTokenPrice() public view returns (uint256) {
        return oracle.latestAnswerInEth();
    }

    /**
     * @notice Returns the latest price in USD.
     */
    function getTokenPriceUsd() public view returns (uint256) {
        return oracle.latestAnswer();
    }

    /**
     * @notice Returns lp token price in ETH.
     */
    function getLpTokenPrice() public view returns (uint) {
        // decis 8
        uint rdntPriceInEth = getTokenPrice();
        return poolHelper.getLpPrice(rdntPriceInEth);
    }

    /**
     * @notice Returns lp token price in USD.
     */
    function getLpTokenPriceUsd() public view returns (uint256 price) {
        // decimals 8
       uint256 lpPriceInEth = getLpTokenPrice();
       // decimals 8
       uint256 ethPrice = uint256(baseTokenPriceInUsdProxyAggregator.latestAnswer());
       price = lpPriceInEth.mul(ethPrice).div(10 ** 8);
    }

    /**
     * @notice Returns lp token address.
     */
    function getLpTokenAddress() public view returns (address) {
        return poolHelper.lpTokenAddr();
    }

    /**
     * @notice Returns decimals of price.
     */
    function decimals() public pure returns (uint256) {
        return 8;
    }
}

contracts/oracles/UniV2TwapOracle.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity 0.7.6;

import "./BaseOracle.sol";

import "@uniswap/v2-core/contracts/interfaces/IUniswapV2Factory.sol";
import "@uniswap/v2-core/contracts/interfaces/IUniswapV2Pair.sol";
import "@uniswap/lib/contracts/libraries/FixedPoint.sol";

import "../dependencies/openzeppelin/contracts/IERC20Detailed.sol";
import "../dependencies/uniswap/contracts/UniswapV2OracleLibrary.sol";
import "../dependencies/uniswap/contracts/UniswapV2Library.sol";
import "../dependencies/openzeppelin/upgradeability/Initializable.sol";

/// @title UniV2TwapOracle Contract
/// @author Radiant team
/// @dev Fixed window oracle that recomputes the average price for the entire period once every period
/// Note that the price average is only guaranteed to be over at least 1 period, but may be over a longer period
contract UniV2TwapOracle is Initializable, BaseOracle {
    using FixedPoint for *;

    /// @notice TWAP period
    uint256 public PERIOD;

    /// @notice Used for being able to consult past the period end
    uint256 public CONSULT_LENIENCY;

    /// @notice If false, consult() will fail if the TWAP is stale
    bool public ALLOW_STALE_CONSULTS;


    /// @notice Uniswap pair contract 
    IUniswapV2Pair public pair;

    /// @notice First token of the pair 
    address public token0;

    /// @notice Second token of the pair 
    address public token1;


    /// @notice First token of the pair 
    uint256 public price0CumulativeLast;

    /// @notice First token of the pair 
    uint256 public price1CumulativeLast;

    /// @notice Last updated timestamp
    uint32 public blockTimestampLast;

    /// @notice Average price of token0
    FixedPoint.uq112x112 public price0Average;

    /// @notice Average price of token1 
    FixedPoint.uq112x112 public price1Average;

    /**
     * @notice Initializer
     * @param _pair Uniswap pair contract
     * @param _rdnt RDNT token address.
     * @param _ethChainlinkFeed Chainlink price feed for ETH.
     * @param _period TWAP period.
     * @param _consultLeniency Used for being able to consult past the period end.
     * @param _allowStaleConsults Enable/Disable stale.
     */
    function initialize(
        address _pair,
        address _rdnt,
        address _ethChainlinkFeed,
        uint256 _period,
        uint256 _consultLeniency,
        bool _allowStaleConsults
    ) external initializer {
        pair = IUniswapV2Pair(_pair);
        token0 = pair.token0();
        token1 = pair.token1();
        price0CumulativeLast = pair.price0CumulativeLast(); // Fetch the current accumulated price value (1 / 0)
        price1CumulativeLast = pair.price1CumulativeLast(); // Fetch the current accumulated price value (0 / 1)
        uint112 reserve0;
        uint112 reserve1;
        (reserve0, reserve1, blockTimestampLast) = pair.getReserves();
        require(
            reserve0 != 0 && reserve1 != 0,
            "UniswapPairOracle: NO_RESERVES"
        ); // Ensure that there's liquidity in the pair

        PERIOD = _period;
        CONSULT_LENIENCY = _consultLeniency;
        ALLOW_STALE_CONSULTS = _allowStaleConsults;

        __BaseOracle_init(_rdnt, _ethChainlinkFeed);
    }

    /**
     * @notice Sets new period.
     * @param _period TWAP period.
     */
    function setPeriod(uint256 _period) external onlyOwner {
        PERIOD = _period;
    }

    /**
     * @notice Sets new consult leniency.
     * @param _consultLeniency new value.
     */
    function setConsultLeniency(uint256 _consultLeniency) external onlyOwner {
        CONSULT_LENIENCY = _consultLeniency;
    }

    /**
     * @notice Sets stale consult option.
     * @param _allowStaleConsults new value.
     */
    function setAllowStaleConsults(bool _allowStaleConsults)
        external
        onlyOwner
    {
        ALLOW_STALE_CONSULTS = _allowStaleConsults;
    }

    /**
     * @dev Check if update() can be called instead of wasting gas calling it.
     */
    function canUpdate() public view override returns (bool) {
        uint32 blockTimestamp = UniswapV2OracleLibrary.currentBlockTimestamp();
        uint32 timeElapsed = blockTimestamp - blockTimestampLast; // Overflow is desired
        return (timeElapsed >= PERIOD);
    }

    /**
     * @notice Updates price
     */
    function update() external {
        (
            uint256 price0Cumulative,
            uint256 price1Cumulative,
            uint32 blockTimestamp
        ) = UniswapV2OracleLibrary.currentCumulativePrices(address(pair));
        uint32 timeElapsed = blockTimestamp - blockTimestampLast; // Overflow is desired

        // Ensure that at least one full period has passed since the last update
        require(timeElapsed >= PERIOD, "UniswapPairOracle: PERIOD_NOT_ELAPSED");

        // Overflow is desired, casting never truncates
        // Cumulative price is in (uq112x112 price * seconds) units so we simply wrap it after division by time elapsed
        price0Average = FixedPoint.uq112x112(
            uint224((price0Cumulative - price0CumulativeLast) / timeElapsed)
        );
        price1Average = FixedPoint.uq112x112(
            uint224((price1Cumulative - price1CumulativeLast) / timeElapsed)
        );
        price0CumulativeLast = price0Cumulative;
        price1CumulativeLast = price1Cumulative;
        blockTimestampLast = blockTimestamp;
    }

    /**
     * @dev This will always return 0 before update has been called successfully for the first time.
     */
    function _consult(address _token, uint256 _amountIn)
        internal
        view
        returns (uint256 amountOut)
    {
        uint32 blockTimestamp = UniswapV2OracleLibrary.currentBlockTimestamp();
        uint32 timeElapsed = blockTimestamp - blockTimestampLast; // Overflow is desired

        // Ensure that the price is not stale
        require(
            (timeElapsed < (PERIOD + CONSULT_LENIENCY)) || ALLOW_STALE_CONSULTS,
            "UniswapPairOracle: PRICE_IS_STALE_NEED_TO_CALL_UPDATE"
        );

        if (_token == token0) {
            amountOut = price0Average.mul(_amountIn).decode144();
        } else {
            require(_token == token1, "UniswapPairOracle: INVALID_TOKEN");
            amountOut = price1Average.mul(_amountIn).decode144();
        }
    }

    /**
     * @notice Returns current price.
     */
    function consult() public view override returns (uint256 amountOut) {
        uint8 decimals = IERC20Detailed(token).decimals();
        return _consult(token, 10**decimals);
    }
}

contracts/oracles/UniV3TwapOracle.sol.content:
pragma solidity 0.7.6;

import "./BaseOracle.sol";

import "../dependencies/openzeppelin/contracts/IERC20Detailed.sol";
import "../dependencies/openzeppelin/contracts/SafeERC20.sol";
import "../dependencies/openzeppelin/contracts/SafeMath.sol";

import "../dependencies/uniswap/contracts/v3/OracleLibrary.sol";
import "../dependencies/uniswap/contracts/v3/IUniswapV3Pool.sol";

import "../dependencies/openzeppelin/upgradeability/Initializable.sol";
import "../dependencies/openzeppelin/upgradeability/OwnableUpgradeable.sol";
import "../dependencies/openzeppelin/upgradeability/PausableUpgradeable.sol";

contract UniV3TwapOracle is Initializable, BaseOracle {
    using SafeMath for uint256;

    /// @notice Uniswap V3 pool address
    IUniswapV3Pool public pool;

    /// @notice First token of the pair 
    IERC20Detailed public token0;

    /// @notice Second token of the pair 
    IERC20Detailed public token1;

    /// @notice Decimal of token0
    uint8 public decimals0;

    /// @notice Decimal of token1
    uint8 public decimals1;

    /// @notice TWAP loopback period
    uint32 public lookbackSecs;

    /// @notice Can flip the order of the pricing
    bool public priceInToken0 = false;

    /**
     * @notice Initializer
     * @param _pair Uniswap pair contract
     * @param _rdnt RDNT token address.
     * @param _ethChainlinkFeed Chainlink price feed for ETH.
     */
    function initialize(
        address _pair,
        address _rdnt,
        address _ethChainlinkFeed,
        uint32 _lookbackSecs
    ) external initializer {
        pool = IUniswapV3Pool(_pair);
        token0 = IERC20Detailed(pool.token0());
        token1 = IERC20Detailed(pool.token1());

        decimals0 = token0.decimals();
        decimals1 = token1.decimals();

        lookbackSecs = _lookbackSecs;

        __BaseOracle_init(_rdnt, _ethChainlinkFeed);
    }

    /* ========== RESTRICTED FUNCTIONS ========== */

    /**
     * @dev Convenience function
     */
    function increaseObservationCardinality(uint16 numCardinals)
        external
        onlyOwner
    {
        pool.increaseObservationCardinalityNext(numCardinals);
    }

    /**
     * @notice Sets new TWAP loopback period
     * @param _secs Loopback period in seconds
     */
    function setTWAPLookbackSec(uint32 _secs) external onlyOwner {
        lookbackSecs = _secs;
    }

    /**
     * @notice Toggles price quote option.
     */
    function toggleTokenForPricing() external onlyOwner {
        priceInToken0 = !priceInToken0;
    }

    /* ========== VIEWS ========== */

    /**
     * @notice Returns token symbols for base and pricing.
     * @return base token symbol
     * @return pricing token symbol
     */
    function tokenSymbols()
        external
        view
        returns (string memory base, string memory pricing)
    {
        if (priceInToken0) {
            base = token1.symbol();
            pricing = token0.symbol();
        } else {
            base = token0.symbol();
            pricing = token1.symbol();
        }
    }

    /**
     * @notice Returns price
     * @return amountOut Price in base token.
     */
    function getPrecisePrice() public view returns (uint256 amountOut) {
        // Get the average price tick first
        (int24 arithmeticMeanTick, ) = OracleLibrary.consult(
            address(pool),
            lookbackSecs
        );

        // Get the quote for selling 1 unit of a token. Assumes 1e18 for both.
        if (priceInToken0) {
            if (decimals0 <= 18) {
                amountOut = OracleLibrary
                    .getQuoteAtTick(
                        arithmeticMeanTick,
                        uint128(10**decimals1),
                        address(token1),
                        address(token0)
                    )
                    .mul(10**(18 - decimals0));
            } else {
                amountOut = OracleLibrary
                    .getQuoteAtTick(
                        arithmeticMeanTick,
                        uint128(10**decimals1),
                        address(token1),
                        address(token0)
                    )
                    .div(10**(decimals0 - 18));
            }
        } else {
            if (decimals1 <= 18) {
                amountOut = OracleLibrary
                    .getQuoteAtTick(
                        arithmeticMeanTick,
                        uint128(10**decimals0),
                        address(token0),
                        address(token1)
                    )
                    .mul(10**(18 - decimals1));
            } else {
                amountOut = OracleLibrary
                    .getQuoteAtTick(
                        arithmeticMeanTick,
                        uint128(10**decimals0),
                        address(token0),
                        address(token1)
                    )
                    .div(10**(decimals1 - 18));
            }
        }
    }

    /**
     * @notice Returns current price.
     */
    function consult() public view override returns (uint256) {
        return getPrecisePrice();
    }

    /**
     * @dev AggregatorV3Interface / Chainlink compatibility.
     */    
    function latestRoundData()
        external
        view
        returns (
            uint80 roundId,
            int256 answer,
            uint256 startedAt,
            uint256 updatedAt,
            uint80 answeredInRound
        )
    {
        return (0, int256(getPrecisePrice()), 0, block.timestamp, 0);
    }
}

