// SPDX-License-Identifier: agpl-3.0
pragma solidity 0.7.6;

library DataTypes {
  // refer to the whitepaper, section 1.1 basic concepts for a formal description of these properties.
  struct ReserveData {
    //stores the reserve configuration
    ReserveConfigurationMap configuration;
    //the liquidity index. Expressed in ray
    uint128 liquidityIndex;
    //variable borrow index. Expressed in ray
    uint128 variableBorrowIndex;
    //the current supply rate. Expressed in ray
    uint128 currentLiquidityRate;
    //the current variable borrow rate. Expressed in ray
    uint128 currentVariableBorrowRate;
    //the current stable borrow rate. Expressed in ray
    uint128 currentStableBorrowRate;
    uint40 lastUpdateTimestamp;
    //tokens addresses
    address aTokenAddress;
    address stableDebtTokenAddress;
    address variableDebtTokenAddress;
    //address of the interest rate strategy
    address interestRateStrategyAddress;
    //the id of the reserve. Represents the position in the list of the active reserves
    uint8 id;
  }

  struct ReserveConfigurationMap {
    //bit 0-15: LTV
    //bit 16-31: Liq. threshold
    //bit 32-47: Liq. bonus
    //bit 48-55: Decimals
    //bit 56: Reserve is active
    //bit 57: reserve is frozen
    //bit 58: borrowing is enabled
    //bit 59: stable rate borrowing enabled
    //bit 60-63: reserved
    //bit 64-79: reserve factor
    uint256 data;
  }

  struct UserConfigurationMap {
    uint256 data;
  }

  enum InterestRateMode {NONE, STABLE, VARIABLE}
}

contracts/protocol/tokenization/AToken.s.content:
// SPDX-License-Identifier: agpl-3.0
pragma solidity 0.7.6;
pragma abicoder v2;

import {IERC20} from '../../dependencies/openzeppelin/contracts/IERC20.sol';
import {SafeERC20} from '../../dependencies/openzeppelin/contracts/SafeERC20.sol';
import {ILendingPool} from '../../interfaces/ILendingPool.sol';
import {IAToken} from '../../interfaces/IAToken.sol';
import {WadRayMath} from '../libraries/math/WadRayMath.sol';
import {Errors} from '../libraries/helpers/Errors.sol';
import {VersionedInitializable} from '../libraries/aave-upgradeability/VersionedInitializable.sol';
import {IncentivizedERC20} from './IncentivizedERC20.sol';
import {IAaveIncentivesController} from '../../interfaces/IAaveIncentivesController.sol';
import {SafeMath} from '../../dependencies/openzeppelin/contracts/SafeMath.sol';
import {IMFDstats} from '../../interfaces/IMFDstats.sol';
import {IMiddleFeeDistribution} from '../../interfaces/IMiddleFeeDistribution.sol';

/**
 * @title Aave ERC20 AToken
 * @dev Implementation of the interest bearing token for the Aave protocol
 * @author Aave
 */
contract AToken is
  VersionedInitializable,
  IncentivizedERC20('ATOKEN_IMPL', 'ATOKEN_IMPL', 0),
  IAToken
{
  using WadRayMath for uint256;
  using SafeERC20 for IERC20;
  using SafeMath for uint256;

  bytes public constant EIP712_REVISION = bytes('1');
  bytes32 internal constant EIP712_DOMAIN =
    keccak256('EIP712Domain(string name,string version,uint256 chainId,address verifyingContract)');
  bytes32 public constant PERMIT_TYPEHASH =
    keccak256('Permit(address owner,address spender,uint256 value,uint256 nonce,uint256 deadline)');

  uint256 public constant ATOKEN_REVISION = 0x1;

  /// @dev owner => next valid nonce to submit with permit()
  mapping(address => uint256) public _nonces;

  bytes32 public DOMAIN_SEPARATOR;

  address internal _treasury;
  IAaveIncentivesController internal _incentivesController;

  event MfdStatsSuccess (string state);

  modifier onlyLendingPool {
    require(_msgSender() == address(_pool), Errors.CT_CALLER_MUST_BE_LENDING_POOL);
    _;
  }

  function getRevision() internal pure virtual override returns (uint256) {
    return ATOKEN_REVISION;
  }

  /**
   * @dev Initializes the aToken
   * @param pool The address of the lending pool where this aToken will be used
   * @param treasury The address of the Aave treasury, receiving the fees on this aToken
   * @param underlyingAsset The address of the underlying asset of this aToken (E.g. WETH for aWETH)
   * @param incentivesController The smart contract managing potential incentives distribution
   * @param aTokenDecimals The decimals of the aToken, same as the underlying asset's
   * @param aTokenName The name of the aToken
   * @param aTokenSymbol The symbol of the aToken
   */
  function initialize(
    ILendingPool pool,
    address treasury,
    address underlyingAsset,
    IAaveIncentivesController incentivesController,
    uint8 aTokenDecimals,
    string calldata aTokenName,
    string calldata aTokenSymbol,
    bytes calldata params
  ) external override initializer {
    uint256 chainId;

    //solium-disable-next-line
    assembly {
      chainId := chainid()
    }

    DOMAIN_SEPARATOR = keccak256(
      abi.encode(
        EIP712_DOMAIN,
        keccak256(bytes(aTokenName)),
        keccak256(EIP712_REVISION),
        chainId,
        address(this)
      )
    );

    _setName(aTokenName);
    _setSymbol(aTokenSymbol);
    _setDecimals(aTokenDecimals);

    _pool = pool;
    _treasury = treasury;
    _underlyingAsset = underlyingAsset;
    _incentivesController = incentivesController;

    emit Initialized(
      underlyingAsset,
      address(pool),
      treasury,
      address(incentivesController),
      aTokenDecimals,
      aTokenName,
      aTokenSymbol,
      params
    );
  }

  /**
   * @dev Burns aTokens from `user` and sends the equivalent amount of underlying to `receiverOfUnderlying`
   * - Only callable by the LendingPool, as extra state updates there need to be managed
   * @param user The owner of the aTokens, getting them burned
   * @param receiverOfUnderlying The address that will receive the underlying
   * @param amount The amount being burned
   * @param index The new liquidity index of the reserve
   **/
  function burn(
    address user,
    address receiverOfUnderlying,
    uint256 amount,
    uint256 index
  ) external override onlyLendingPool {
    uint256 amountScaled = amount.rayDiv(index);
    require(amountScaled != 0, Errors.CT_INVALID_BURN_AMOUNT);
    _burn(user, amountScaled);

    IERC20(_underlyingAsset).safeTransfer(receiverOfUnderlying, amount);

    emit Transfer(user, address(0), amount);
    emit Burn(user, receiverOfUnderlying, amount, index);
  }

  /**
   * @dev Mints `amount` aTokens to `user`
   * - Only callable by the LendingPool, as extra state updates there need to be managed
   * @param user The address receiving the minted tokens
   * @param amount The amount of tokens getting minted
   * @param index The new liquidity index of the reserve
   * @return `true` if the the previous balance of the user was 0
   */
  function mint(
    address user,
    uint256 amount,
    uint256 index
  ) external override onlyLendingPool returns (bool) {
    uint256 previousBalance = super.balanceOf(user);

    uint256 amountScaled = amount.rayDiv(index);
    require(amountScaled != 0, Errors.CT_INVALID_MINT_AMOUNT);
    _mint(user, amountScaled);

    emit Transfer(address(0), user, amount);
    emit Mint(user, amount, index);

    return previousBalance == 0;
  }

  /**
   * @dev Mints aTokens to the reserve treasury
   * - Only callable by the LendingPool
   * @param amount The amount of tokens getting minted
   * @param index The new liquidity index of the reserve
   */
  function mintToTreasury(uint256 amount, uint256 index) external override onlyLendingPool {
    if (amount == 0) {
      return;
    }

    address treasury = _treasury;

    // Compared to the normal mint, we don't check for rounding errors.
    // The amount to mint can easily be very small since it is a fraction of the interest ccrued.
    // In that case, the treasury will experience a (very small) loss, but it
    // wont cause potentially valid transactions to fail.
    _mint(treasury, amount.rayDiv(index));

    try IMiddleFeeDistribution(treasury).getMFDstatsAddress() returns (address mfdStats) {
        try IMFDstats(mfdStats).addTransfer(IMFDstats.AddTransferParam(_underlyingAsset, amount, treasury)) {
          emit MfdStatsSuccess("Success!");
        } catch {
          emit MfdStatsSuccess("Add Transfer Failed!");
        }
    } catch {
        emit MfdStatsSuccess("Get MFDstats Address Failed!");
    }

    emit Transfer(address(0), treasury, amount);
    emit Mint(treasury, amount, index);
  }

  /**
   * @dev Transfers aTokens in the event of a borrow being liquidated, in case the liquidators reclaims the aToken
   * - Only callable by the LendingPool
   * @param from The address getting liquidated, current owner of the aTokens
   * @param to The recipient
   * @param value The amount of tokens getting transferred
   **/
  function transferOnLiquidation(
    address from,
    address to,
    uint256 value
  ) external override onlyLendingPool {
    // Being a normal transfer, the Transfer() and BalanceTransfer() are emitted
    // so no need to emit a specific event here
    _transfer(from, to, value, false);

    emit Transfer(from, to, value);
  }

  /**
   * @dev Calculates the balance of the user: principal balance + interest generated by the principal
   * @param user The user whose balance is calculated
   * @return The balance of the user
   **/
  function balanceOf(address user)
    public
    view
    override(IncentivizedERC20, IERC20)
    returns (uint256)
  {
    return super.balanceOf(user).rayMul(_pool.getReserveNormalizedIncome(_underlyingAsset));
  }

  /**
   * @dev Returns the scaled balance of the user. The scaled balance is the sum of all the
   * updated stored balance divided by the reserve's liquidity index at the moment of the update
   * @param user The user whose balance is calculated
   * @return The scaled balance of the user
   **/
  function scaledBalanceOf(address user) external view override returns (uint256) {
    return super.balanceOf(user);
  }

  /**
   * @dev Returns the scaled balance of the user and the scaled total supply.
   * @param user The address of the user
   * @return The scaled balance of the user
   * @return The scaled balance and the scaled total supply
   **/
  function getScaledUserBalanceAndSupply(address user)
    external
    view
    override
    returns (uint256, uint256)
  {
    return (super.balanceOf(user), super.totalSupply());
  }

  /**
   * @dev calculates the total supply of the specific aToken
   * since the balance of every single user increases over time, the total supply
   * does that too.
   * @return the current total supply
   **/
  function totalSupply() public view override(IncentivizedERC20, IERC20) returns (uint256) {
    uint256 currentSupplyScaled = super.totalSupply();

    if (currentSupplyScaled == 0) {
      return 0;
    }

    return currentSupplyScaled.rayMul(_pool.getReserveNormalizedIncome(_underlyingAsset));
  }

  /**
   * @dev Returns the scaled total supply of the variable debt token. Represents sum(debt/index)
   * @return the scaled total supply
   **/
  function scaledTotalSupply() public view virtual override returns (uint256) {
    return super.totalSupply();
  }

  /**
   * @dev Returns the address of the Aave treasury, receiving the fees on this aToken
   **/
  function RESERVE_TREASURY_ADDRESS() public view returns (address) {
    return _treasury;
  }

  /**
   * @dev Returns the address of the underlying asset of this aToken (E.g. WETH for aWETH)
   **/
  function UNDERLYING_ASSET_ADDRESS() public override view returns (address) {
    return _underlyingAsset;
  }

  /**
   * @dev Returns the address of the lending pool where this aToken is used
   **/
  function POOL() public view returns (ILendingPool) {
    return _pool;
  }

  /**
   * @dev For internal usage in the logic of the parent contract IncentivizedERC20
   **/
  function _getIncentivesController() internal view override returns (IAaveIncentivesController) {
    return _incentivesController;
  }

  /**
   * @dev Returns the address of the incentives controller contract
   **/
  function getIncentivesController() external view override returns (IAaveIncentivesController) {
    return _getIncentivesController();
  }

  /**
   * @dev Transfers the underlying asset to `target`. Used by the LendingPool to transfer
   * assets in borrow(), withdraw() and flashLoan()
   * @param target The recipient of the aTokens
   * @param amount The amount getting transferred
   * @return The amount transferred
   **/
  function transferUnderlyingTo(address target, uint256 amount)
    external
    override
    onlyLendingPool
    returns (uint256)
  {
    IERC20(_underlyingAsset).safeTransfer(target, amount);
    return amount;
  }

  /**
   * @dev Invoked to execute actions on the aToken side after a repayment.
   * @param user The user executing the repayment
   * @param amount The amount getting repaid
   **/
  function handleRepayment(address user, uint256 amount) external override onlyLendingPool {}

  /**
   * @dev implements the permit function as for
   * https://github.com/ethereum/EIPs/blob/8a34d644aacf0f9f8f00815307fd7dd5da07655f/EIPS/eip-2612.md
   * @param owner The owner of the funds
   * @param spender The spender
   * @param value The amount
   * @param deadline The deadline timestamp, type(uint256).max for max deadline
   * @param v Signature param
   * @param s Signature param
   * @param r Signature param
   */
  function permit(
    address owner,
    address spender,
    uint256 value,
    uint256 deadline,
    uint8 v,
    bytes32 r,
    bytes32 s
  ) external {
    require(owner != address(0), 'INVALID_OWNER');
    //solium-disable-next-line
    require(block.timestamp <= deadline, 'INVALID_EXPIRATION');
    uint256 currentValidNonce = _nonces[owner];
    bytes32 digest =
      keccak256(
        abi.encodePacked(
          '\x19\x01',
          DOMAIN_SEPARATOR,
          keccak256(abi.encode(PERMIT_TYPEHASH, owner, spender, value, currentValidNonce, deadline))
        )
      );
    require(owner == ecrecover(digest, v, r, s), 'INVALID_SIGNATURE');
    _nonces[owner] = currentValidNonce.add(1);
    _approve(owner, spender, value);
  }

  /**
   * @dev Transfers the aTokens between two users. Validates the transfer
   * (ie checks for valid HF after the transfer) if required
   * @param from The source address
   * @param to The destination address
   * @param amount The amount getting transferred
   * @param validate `true` if the transfer needs to be validated
   **/
  function _transfer(
    address from,
    address to,
    uint256 amount,
    bool validate
  ) internal {
    address underlyingAsset = _underlyingAsset;
    ILendingPool pool = _pool;

    uint256 index = pool.getReserveNormalizedIncome(underlyingAsset);

    uint256 fromBalanceBefore = super.balanceOf(from).rayMul(index);
    uint256 toBalanceBefore = super.balanceOf(to).rayMul(index);

    super._transfer(from, to, amount.rayDiv(index));

    if (validate) {
      pool.finalizeTransfer(underlyingAsset, from, to, amount, fromBalanceBefore, toBalanceBefore);
    }

    emit BalanceTransfer(from, to, amount, index);
  }

  /**
   * @dev Overrides the parent _transfer to force validated transfer() and transferFrom()
   * @param from The source address
   * @param to The destination address
   * @param amount The amount getting transferred
   **/
  function _transfer(
    address from,
    address to,
    uint256 amount
  ) internal override {
    _transfer(from, to, amount, true);
  }
}

contracts/protocol/tokenization/base/Deb.content:
// SPDX-License-Identifier: agpl-3.0
pragma solidity 0.7.6;

import {ILendingPool} from '../../../interfaces/ILendingPool.sol';
import {ICreditDelegationToken} from '../../../interfaces/ICreditDelegationToken.sol';
import {
  VersionedInitializable
} from '../../libraries/aave-upgradeability/VersionedInitializable.sol';
import {IncentivizedERC20} from '../IncentivizedERC20.sol';
import {Errors} from '../../libraries/helpers/Errors.sol';
import {SafeMath} from '../../../dependencies/openzeppelin/contracts/SafeMath.sol';

/**
 * @title DebtTokenBase
 * @notice Base contract for different types of debt tokens, like StableDebtToken or VariableDebtToken
 * @author Aave
 */

abstract contract DebtTokenBase is
  IncentivizedERC20('DEBTTOKEN_IMPL', 'DEBTTOKEN_IMPL', 0),
  VersionedInitializable,
  ICreditDelegationToken
{
  using SafeMath for uint256;

  mapping(address => mapping(address => uint256)) internal _borrowAllowances;

  /**
   * @dev Only lending pool can call functions marked by this modifier
   **/
  modifier onlyLendingPool {
    require(_msgSender() == address(_getLendingPool()), Errors.CT_CALLER_MUST_BE_LENDING_POOL);
    _;
  }

  /**
   * @dev delegates borrowing power to a user on the specific debt token
   * @param delegatee the address receiving the delegated borrowing power
   * @param amount the maximum amount being delegated. Delegation will still
   * respect the liquidation constraints (even if delegated, a delegatee cannot
   * force a delegator HF to go below 1)
   **/
  function approveDelegation(address delegatee, uint256 amount) external override {
    _borrowAllowances[_msgSender()][delegatee] = amount;
    emit BorrowAllowanceDelegated(_msgSender(), delegatee, _getUnderlyingAssetAddress(), amount);
  }

  /**
   * @dev returns the borrow allowance of the user
   * @param fromUser The user to giving allowance
   * @param toUser The user to give allowance to
   * @return the current allowance of toUser
   **/
  function borrowAllowance(address fromUser, address toUser)
    external
    view
    override
    returns (uint256)
  {
    return _borrowAllowances[fromUser][toUser];
  }

  /**
   * @dev Being non transferrable, the debt token does not implement any of the
   * standard ERC20 functions for transfer and allowance.
   **/
  function transfer(address recipient, uint256 amount) public virtual override returns (bool) {
    recipient;
    amount;
    revert('TRANSFER_NOT_SUPPORTED');
  }

  function allowance(address owner, address spender)
    public
    view
    virtual
    override
    returns (uint256)
  {
    owner;
    spender;
    revert('ALLOWANCE_NOT_SUPPORTED');
  }

  function approve(address spender, uint256 amount) public virtual override returns (bool) {
    spender;
    amount;
    revert('APPROVAL_NOT_SUPPORTED');
  }

  function transferFrom(
    address sender,
    address recipient,
    uint256 amount
  ) public virtual override returns (bool) {
    sender;
    recipient;
    amount;
    revert('TRANSFER_NOT_SUPPORTED');
  }

  function increaseAllowance(address spender, uint256 addedValue)
    public
    virtual
    override
    returns (bool)
  {
    spender;
    addedValue;
    revert('ALLOWANCE_NOT_SUPPORTED');
  }

  function decreaseAllowance(address spender, uint256 subtractedValue)
    public
    virtual
    override
    returns (bool)
  {
    spender;
    subtractedValue;
    revert('ALLOWANCE_NOT_SUPPORTED');
  }

  function _decreaseBorrowAllowance(
    address delegator,
    address delegatee,
    uint256 amount
  ) internal {
    uint256 newAllowance =
      _borrowAllowances[delegator][delegatee].sub(amount, Errors.BORROW_ALLOWANCE_NOT_ENOUGH);

    _borrowAllowances[delegator][delegatee] = newAllowance;

    emit BorrowAllowanceDelegated(delegator, delegatee, _getUnderlyingAssetAddress(), newAllowance);
  }

  function _getUnderlyingAssetAddress() internal view virtual returns (address);

  function _getLendingPool() internal view virtual returns (ILendingPool);
}

contracts/protocol/tokenization/Incentiv.content:
// SPDX-License-Identifier: agpl-3.0
pragma solidity 0.7.6;

import {Context} from '../../dependencies/openzeppelin/contracts/Context.sol';
import {IERC20} from '../../dependencies/openzeppelin/contracts/IERC20.sol';
import {IERC20Detailed} from '../../dependencies/openzeppelin/contracts/IERC20Detailed.sol';
import {SafeMath} from '../../dependencies/openzeppelin/contracts/SafeMath.sol';
import {IAaveIncentivesController} from '../../interfaces/IAaveIncentivesController.sol';
import {ILendingPoolAddressesProvider} from '../../interfaces/ILendingPoolAddressesProvider.sol';
import {IPriceOracle} from '../../interfaces/IPriceOracle.sol';
import {ILendingPool} from '../../interfaces/ILendingPool.sol';

/**
 * @title ERC20
 * @notice Basic ERC20 implementation
 * @author Aave, inspired by the Openzeppelin ERC20 implementation
 **/
abstract contract IncentivizedERC20 is Context, IERC20, IERC20Detailed {
  using SafeMath for uint256;

  mapping(address => uint256) internal _balances;

  mapping(address => mapping(address => uint256)) private _allowances;
  uint256 internal _totalSupply;
  string private _name;
  string private _symbol;
  uint8 private _decimals;

  ILendingPool internal _pool;
  address internal _underlyingAsset;

  constructor(
    string memory name_,
    string memory symbol_,
    uint8 decimals_
  ) {
    _name = name_;
    _symbol = symbol_;
    _decimals = decimals_;
  }

  /**
   * @return The name of the token
   **/
  function name() public view override returns (string memory) {
    return _name;
  }

  /**
   * @return The symbol of the token
   **/
  function symbol() public view override returns (string memory) {
    return _symbol;
  }

  /**
   * @return The decimals of the token
   **/
  function decimals() public view override returns (uint8) {
    return _decimals;
  }

  /**
   * @return The total supply of the token
   **/
  function totalSupply() public view virtual override returns (uint256) {
    return _totalSupply;
  }

  /**
   * @return The balance of the token
   **/
  function balanceOf(address account) public view virtual override returns (uint256) {
    return _balances[account];
  }

  /**
   * @return Abstract function implemented by the child aToken/debtToken.
   * Done this way in order to not break compatibility with previous versions of aTokens/debtTokens
   **/
  function _getIncentivesController() internal view virtual returns(IAaveIncentivesController);

  /**
   * @dev Executes a transfer of tokens from _msgSender() to recipient
   * @param recipient The recipient of the tokens
   * @param amount The amount of tokens being transferred
   * @return `true` if the transfer succeeds, `false` otherwise
   **/
  function transfer(address recipient, uint256 amount) public virtual override returns (bool) {
    _transfer(_msgSender(), recipient, amount);
    emit Transfer(_msgSender(), recipient, amount);
    return true;
  }

  /**
   * @dev Returns the allowance of spender on the tokens owned by owner
   * @param owner The owner of the tokens
   * @param spender The user allowed to spend the owner's tokens
   * @return The amount of owner's tokens spender is allowed to spend
   **/
  function allowance(address owner, address spender)
    public
    view
    virtual
    override
    returns (uint256)
  {
    return _allowances[owner][spender];
  }

  /**
   * @dev Allows `spender` to spend the tokens owned by _msgSender()
   * @param spender The user allowed to spend _msgSender() tokens
   * @return `true`
   **/
  function approve(address spender, uint256 amount) public virtual override returns (bool) {
    _approve(_msgSender(), spender, amount);
    return true;
  }

  /**
   * @dev Executes a transfer of token from sender to recipient, if _msgSender() is allowed to do so
   * @param sender The owner of the tokens
   * @param recipient The recipient of the tokens
   * @param amount The amount of tokens being transferred
   * @return `true` if the transfer succeeds, `false` otherwise
   **/
  function transferFrom(
    address sender,
    address recipient,
    uint256 amount
  ) public virtual override returns (bool) {
    _transfer(sender, recipient, amount);
    _approve(
      sender,
      _msgSender(),
      _allowances[sender][_msgSender()].sub(amount, 'ERC20: transfer amount exceeds allowance')
    );
    emit Transfer(sender, recipient, amount);
    return true;
  }

  /**
   * @dev Increases the allowance of spender to spend _msgSender() tokens
   * @param spender The user allowed to spend on behalf of _msgSender()
   * @param addedValue The amount being added to the allowance
   * @return `true`
   **/
  function increaseAllowance(address spender, uint256 addedValue) public virtual returns (bool) {
    _approve(_msgSender(), spender, _allowances[_msgSender()][spender].add(addedValue));
    return true;
  }

  /**
   * @dev Decreases the allowance of spender to spend _msgSender() tokens
   * @param spender The user allowed to spend on behalf of _msgSender()
   * @param subtractedValue The amount being subtracted to the allowance
   * @return `true`
   **/
  function decreaseAllowance(address spender, uint256 subtractedValue)
    public
    virtual
    returns (bool)
  {
    _approve(
      _msgSender(),
      spender,
      _allowances[_msgSender()][spender].sub(
        subtractedValue,
        'ERC20: decreased allowance below zero'
      )
    );
    return true;
  }

  function _transfer(
    address sender,
    address recipient,
    uint256 amount
  ) internal virtual {
    require(sender != address(0), 'ERC20: transfer from the zero address');
    require(recipient != address(0), 'ERC20: transfer to the zero address');

    _beforeTokenTransfer(sender, recipient, amount);

    uint256 senderBalance = _balances[sender].sub(amount, 'ERC20: transfer amount exceeds balance');

    if (address(_getIncentivesController()) != address(0)) {
      // uint256 currentTotalSupply = _totalSupply;
      _getIncentivesController().handleActionBefore(sender);
      if (sender != recipient) {
        _getIncentivesController().handleActionBefore(recipient);
      }
    }

    _balances[sender] = senderBalance;
    uint256 recipientBalance = _balances[recipient].add(amount);
    _balances[recipient] = recipientBalance;

    if (address(_getIncentivesController()) != address(0)) {
      uint256 currentTotalSupply = _totalSupply;
      _getIncentivesController().handleActionAfter(sender, senderBalance, currentTotalSupply);
      if (sender != recipient) {
        _getIncentivesController().handleActionAfter(recipient, recipientBalance, currentTotalSupply);
      }
    }
  }

  function _mint(address account, uint256 amount) internal virtual {
    require(account != address(0), 'ERC20: mint to the zero address');

    _beforeTokenTransfer(address(0), account, amount);

    uint256 currentTotalSupply = _totalSupply.add(amount);
    uint256 accountBalance = _balances[account].add(amount);
    if (address(_getIncentivesController()) != address(0)) {
      _getIncentivesController().handleActionBefore(account);
    }
    _totalSupply = currentTotalSupply;
    _balances[account] = accountBalance;
    if (address(_getIncentivesController()) != address(0)) {
      _getIncentivesController().handleActionAfter(account, accountBalance, currentTotalSupply);
    }
  }

  function _burn(address account, uint256 amount) internal virtual {
    require(account != address(0), 'ERC20: burn from the zero address');

    _beforeTokenTransfer(account, address(0), amount);

    uint256 currentTotalSupply = _totalSupply.sub(amount);
    uint256 accountBalance = _balances[account].sub(amount, 'ERC20: burn amount exceeds balance');

    if (address(_getIncentivesController()) != address(0)) {
      _getIncentivesController().handleActionBefore(account);
    }

    _totalSupply = currentTotalSupply;
    _balances[account] = accountBalance;

    if (address(_getIncentivesController()) != address(0)) {
      _getIncentivesController().handleActionAfter(account, accountBalance, currentTotalSupply);
    }
  }

  function _approve(
    address owner,
    address spender,
    uint256 amount
  ) internal virtual {
    require(owner != address(0), 'ERC20: approve from the zero address');
    require(spender != address(0), 'ERC20: approve to the zero address');

    _allowances[owner][spender] = amount;
    emit Approval(owner, spender, amount);
  }

  function _setName(string memory newName) internal {
    _name = newName;
  }

  function _setSymbol(string memory newSymbol) internal {
    _symbol = newSymbol;
  }

  function _setDecimals(uint8 newDecimals) internal {
    _decimals = newDecimals;
  }

  function _beforeTokenTransfer(
    address from,
    address to,
    uint256 amount
  ) internal virtual {}

  function getAssetPrice() external view returns (uint256) {
      ILendingPoolAddressesProvider provider = _pool.getAddressesProvider();
      address oracle = provider.getPriceOracle();
      return IPriceOracle(oracle).getAssetPrice(_underlyingAsset);
  }
}

contracts/protocol/tokenization/StableDe.content:
// SPDX-License-Identifier: agpl-3.0
pragma solidity 0.7.6;

import {DebtTokenBase} from './base/DebtTokenBase.sol';
import {MathUtils} from '../libraries/math/MathUtils.sol';
import {WadRayMath} from '../libraries/math/WadRayMath.sol';
import {IStableDebtToken} from '../../interfaces/IStableDebtToken.sol';
import {ILendingPool} from '../../interfaces/ILendingPool.sol';
import {IAaveIncentivesController} from '../../interfaces/IAaveIncentivesController.sol';
import {Errors} from '../libraries/helpers/Errors.sol';
import {SafeMath} from '../../dependencies/openzeppelin/contracts/SafeMath.sol';

/**
 * @title StableDebtToken
 * @notice Implements a stable debt token to track the borrowing positions of users
 * at stable rate mode
 * @author Aave
 **/
contract StableDebtToken is IStableDebtToken, DebtTokenBase {
  using WadRayMath for uint256;
  using SafeMath for uint256;

  uint256 public constant DEBT_TOKEN_REVISION = 0x1;

  uint256 internal _avgStableRate;
  mapping(address => uint40) internal _timestamps;
  mapping(address => uint256) internal _usersStableRate;
  uint40 internal _totalSupplyTimestamp;

  IAaveIncentivesController internal _incentivesController;

  /**
   * @dev Initializes the debt token.
   * @param pool The address of the lending pool where this aToken will be used
   * @param underlyingAsset The address of the underlying asset of this aToken (E.g. WETH for aWETH)
   * @param incentivesController The smart contract managing potential incentives distribution
   * @param debtTokenDecimals The decimals of the debtToken, same as the underlying asset's
   * @param debtTokenName The name of the token
   * @param debtTokenSymbol The symbol of the token
   */
  function initialize(
    ILendingPool pool,
    address underlyingAsset,
    IAaveIncentivesController incentivesController,
    uint8 debtTokenDecimals,
    string memory debtTokenName,
    string memory debtTokenSymbol,
    bytes calldata params
  ) public override initializer {
    _setName(debtTokenName);
    _setSymbol(debtTokenSymbol);
    _setDecimals(debtTokenDecimals);

    _pool = pool;
    _underlyingAsset = underlyingAsset;
    _incentivesController = incentivesController;

    emit Initialized(
      underlyingAsset,
      address(pool),
      address(incentivesController),
      debtTokenDecimals,
      debtTokenName,
      debtTokenSymbol,
      params
    );
  }

  /**
   * @dev Gets the revision of the stable debt token implementation
   * @return The debt token implementation revision
   **/
  function getRevision() internal pure virtual override returns (uint256) {
    return DEBT_TOKEN_REVISION;
  }

  /**
   * @dev Returns the average stable rate across all the stable rate debt
   * @return the average stable rate
   **/
  function getAverageStableRate() external view virtual override returns (uint256) {
    return _avgStableRate;
  }

  /**
   * @dev Returns the timestamp of the last user action
   * @return The last update timestamp
   **/
  function getUserLastUpdated(address user) external view virtual override returns (uint40) {
    return _timestamps[user];
  }

  /**
   * @dev Returns the stable rate of the user
   * @param user The address of the user
   * @return The stable rate of user
   **/
  function getUserStableRate(address user) external view virtual override returns (uint256) {
    return _usersStableRate[user];
  }

  /**
   * @dev Calculates the current user debt balance
   * @return The accumulated debt of the user
   **/
  function balanceOf(address account) public view virtual override returns (uint256) {
    uint256 accountBalance = super.balanceOf(account);
    uint256 stableRate = _usersStableRate[account];
    if (accountBalance == 0) {
      return 0;
    }
    uint256 cumulatedInterest =
      MathUtils.calculateCompoundedInterest(stableRate, _timestamps[account]);
    return accountBalance.rayMul(cumulatedInterest);
  }

  struct MintLocalVars {
    uint256 previousSupply;
    uint256 nextSupply;
    uint256 amountInRay;
    uint256 newStableRate;
    uint256 currentAvgStableRate;
  }

  /**
   * @dev Mints debt token to the `onBehalfOf` address.
   * -  Only callable by the LendingPool
   * - The resulting rate is the weighted average between the rate of the new debt
   * and the rate of the previous debt
   * @param user The address receiving the borrowed underlying, being the delegatee in case
   * of credit delegate, or same as `onBehalfOf` otherwise
   * @param onBehalfOf The address receiving the debt tokens
   * @param amount The amount of debt tokens to mint
   * @param rate The rate of the debt being minted
   **/
  function mint(
    address user,
    address onBehalfOf,
    uint256 amount,
    uint256 rate
  ) external override onlyLendingPool returns (bool) {
    MintLocalVars memory vars;

    if (user != onBehalfOf) {
      _decreaseBorrowAllowance(onBehalfOf, user, amount);
    }

    (, uint256 currentBalance, uint256 balanceIncrease) = _calculateBalanceIncrease(onBehalfOf);

    vars.previousSupply = totalSupply();
    vars.currentAvgStableRate = _avgStableRate;
    vars.nextSupply = _totalSupply = vars.previousSupply.add(amount);

    vars.amountInRay = amount.wadToRay();

    vars.newStableRate = _usersStableRate[onBehalfOf]
      .rayMul(currentBalance.wadToRay())
      .add(vars.amountInRay.rayMul(rate))
      .rayDiv(currentBalance.add(amount).wadToRay());

    require(vars.newStableRate <= type(uint128).max, Errors.SDT_STABLE_DEBT_OVERFLOW);
    _usersStableRate[onBehalfOf] = vars.newStableRate;

    //solium-disable-next-line
    _totalSupplyTimestamp = _timestamps[onBehalfOf] = uint40(block.timestamp);

    // Calculates the updated average stable rate
    vars.currentAvgStableRate = _avgStableRate = vars
      .currentAvgStableRate
      .rayMul(vars.previousSupply.wadToRay())
      .add(rate.rayMul(vars.amountInRay))
      .rayDiv(vars.nextSupply.wadToRay());

    _mint(onBehalfOf, amount.add(balanceIncrease), vars.previousSupply);

    emit Transfer(address(0), onBehalfOf, amount);

    emit Mint(
      user,
      onBehalfOf,
      amount,
      currentBalance,
      balanceIncrease,
      vars.newStableRate,
      vars.currentAvgStableRate,
      vars.nextSupply
    );

    return currentBalance == 0;
  }

  /**
   * @dev Burns debt of `user`
   * @param user The address of the user getting his debt burned
   * @param amount The amount of debt tokens getting burned
   **/
  function burn(address user, uint256 amount) external override onlyLendingPool {
    (, uint256 currentBalance, uint256 balanceIncrease) = _calculateBalanceIncrease(user);

    uint256 previousSupply = totalSupply();
    uint256 newAvgStableRate = 0;
    uint256 nextSupply = 0;
    uint256 userStableRate = _usersStableRate[user];

    // Since the total supply and each single user debt accrue separately,
    // there might be accumulation errors so that the last borrower repaying
    // mght actually try to repay more than the available debt supply.
    // In this case we simply set the total supply and the avg stable rate to 0
    if (previousSupply <= amount) {
      _avgStableRate = 0;
      _totalSupply = 0;
    } else {
      nextSupply = _totalSupply = previousSupply.sub(amount);
      uint256 firstTerm = _avgStableRate.rayMul(previousSupply.wadToRay());
      uint256 secondTerm = userStableRate.rayMul(amount.wadToRay());

      // For the same reason described above, when the last user is repaying it might
      // happen that user rate * user balance > avg rate * total supply. In that case,
      // we simply set the avg rate to 0
      if (secondTerm >= firstTerm) {
        newAvgStableRate = _avgStableRate = _totalSupply = 0;
      } else {
        newAvgStableRate = _avgStableRate = firstTerm.sub(secondTerm).rayDiv(nextSupply.wadToRay());
      }
    }

    if (amount == currentBalance) {
      _usersStableRate[user] = 0;
      _timestamps[user] = 0;
    } else {
      //solium-disable-next-line
      _timestamps[user] = uint40(block.timestamp);
    }
    //solium-disable-next-line
    _totalSupplyTimestamp = uint40(block.timestamp);

    if (balanceIncrease > amount) {
      uint256 amountToMint = balanceIncrease.sub(amount);
      _mint(user, amountToMint, previousSupply);
      emit Mint(
        user,
        user,
        amountToMint,
        currentBalance,
        balanceIncrease,
        userStableRate,
        newAvgStableRate,
        nextSupply
      );
    } else {
      uint256 amountToBurn = amount.sub(balanceIncrease);
      _burn(user, amountToBurn, previousSupply);
      emit Burn(user, amountToBurn, currentBalance, balanceIncrease, newAvgStableRate, nextSupply);
    }

    emit Transfer(user, address(0), amount);
  }

  /**
   * @dev Calculates the increase in balance since the last user interaction
   * @param user The address of the user for which the interest is being accumulated
   * @return The previous principal balance, the new principal balance and the balance increase
   **/
  function _calculateBalanceIncrease(address user)
    internal
    view
    returns (
      uint256,
      uint256,
      uint256
    )
  {
    uint256 previousPrincipalBalance = super.balanceOf(user);

    if (previousPrincipalBalance == 0) {
      return (0, 0, 0);
    }

    // Calculation of the accrued interest since the last accumulation
    uint256 balanceIncrease = balanceOf(user).sub(previousPrincipalBalance);

    return (
      previousPrincipalBalance,
      previousPrincipalBalance.add(balanceIncrease),
      balanceIncrease
    );
  }

  /**
   * @dev Returns the principal and total supply, the average borrow rate and the last supply update timestamp
   **/
  function getSupplyData()
    public
    view
    override
    returns (
      uint256,
      uint256,
      uint256,
      uint40
    )
  {
    uint256 avgRate = _avgStableRate;
    return (super.totalSupply(), _calcTotalSupply(avgRate), avgRate, _totalSupplyTimestamp);
  }

  /**
   * @dev Returns the the total supply and the average stable rate
   **/
  function getTotalSupplyAndAvgRate() public view override returns (uint256, uint256) {
    uint256 avgRate = _avgStableRate;
    return (_calcTotalSupply(avgRate), avgRate);
  }

  /**
   * @dev Returns the total supply
   **/
  function totalSupply() public view override returns (uint256) {
    return _calcTotalSupply(_avgStableRate);
  }

  /**
   * @dev Returns the timestamp at which the total supply was updated
   **/
  function getTotalSupplyLastUpdated() public view override returns (uint40) {
    return _totalSupplyTimestamp;
  }

  /**
   * @dev Returns the principal debt balance of the user from
   * @param user The user's address
   * @return The debt balance of the user since the last burn/mint action
   **/
  function principalBalanceOf(address user) external view virtual override returns (uint256) {
    return super.balanceOf(user);
  }

  /**
   * @dev Returns the address of the underlying asset of this aToken (E.g. WETH for aWETH)
   **/
  function UNDERLYING_ASSET_ADDRESS() public view returns (address) {
    return _underlyingAsset;
  }

  /**
   * @dev Returns the address of the lending pool where this aToken is used
   **/
  function POOL() public view returns (ILendingPool) {
    return _pool;
  }

  /**
   * @dev Returns the address of the incentives controller contract
   **/
  function getIncentivesController() external view override returns (IAaveIncentivesController) {
    return _getIncentivesController();
  }

  /**
   * @dev For internal usage in the logic of the parent contracts
   **/
  function _getIncentivesController() internal view override returns (IAaveIncentivesController) {
    return _incentivesController;
  }

  /**
   * @dev For internal usage in the logic of the parent contracts
   **/
  function _getUnderlyingAssetAddress() internal view override returns (address) {
    return _underlyingAsset;
  }

  /**
   * @dev For internal usage in the logic of the parent contracts
   **/
  function _getLendingPool() internal view override returns (ILendingPool) {
    return _pool;
  }

  /**
   * @dev Calculates the total supply
   * @param avgRate The average rate at which the total supply increases
   * @return The debt balance of the user since the last burn/mint action
   **/
  function _calcTotalSupply(uint256 avgRate) internal view virtual returns (uint256) {
    uint256 principalSupply = super.totalSupply();

    if (principalSupply == 0) {
      return 0;
    }

    uint256 cumulatedInterest =
      MathUtils.calculateCompoundedInterest(avgRate, _totalSupplyTimestamp);

    return principalSupply.rayMul(cumulatedInterest);
  }

  /**
   * @dev Mints stable debt tokens to an user
   * @param account The account receiving the debt tokens
   * @param amount The amount being minted
   * @param oldTotalSupply the total supply before the minting event
   **/
  function _mint(
    address account,
    uint256 amount,
    uint256 oldTotalSupply
  ) internal {
    uint256 oldAccountBalance = _balances[account];
    if (address(_incentivesController) != address(0)) {
      _incentivesController.handleActionBefore(account);
    }
    _balances[account] = oldAccountBalance.add(amount);
    if (address(_incentivesController) != address(0)) {
      _incentivesController.handleActionAfter(account, oldAccountBalance, oldTotalSupply);
    }
  }

  /**
   * @dev Burns stable debt tokens of an user
   * @param account The user getting his debt burned
   * @param amount The amount being burned
   * @param oldTotalSupply The total supply before the burning event
   **/
  function _burn(
    address account,
    uint256 amount,
    uint256 oldTotalSupply
  ) internal {
    uint256 oldAccountBalance = _balances[account];
    if (address(_incentivesController) != address(0)) {
      _incentivesController.handleActionBefore(account);
    }
    _balances[account] = oldAccountBalance.sub(amount, Errors.SDT_BURN_EXCEEDS_BALANCE);
    if (address(_incentivesController) != address(0)) {
      _incentivesController.handleActionAfter(account, oldAccountBalance, oldTotalSupply);
    }
  }
}

contracts/protocol/tokenization/Variable.content:
// SPDX-License-Identifier: agpl-3.0
pragma solidity 0.7.6;

import {IVariableDebtToken} from '../../interfaces/IVariableDebtToken.sol';
import {WadRayMath} from '../libraries/math/WadRayMath.sol';
import {Errors} from '../libraries/helpers/Errors.sol';
import {DebtTokenBase} from './base/DebtTokenBase.sol';
import {ILendingPool} from '../../interfaces/ILendingPool.sol';
import {IAaveIncentivesController} from '../../interfaces/IAaveIncentivesController.sol';
import {SafeMath} from '../../dependencies/openzeppelin/contracts/SafeMath.sol';

/**
 * @title VariableDebtToken
 * @notice Implements a variable debt token to track the borrowing positions of users
 * at variable rate mode
 * @author Aave
 **/
contract VariableDebtToken is DebtTokenBase, IVariableDebtToken {
  using WadRayMath for uint256;
  using SafeMath for uint256;

  uint256 public constant DEBT_TOKEN_REVISION = 0x1;

  IAaveIncentivesController internal _incentivesController;

  /**
   * @dev Initializes the debt token.
   * @param pool The address of the lending pool where this aToken will be used
   * @param underlyingAsset The address of the underlying asset of this aToken (E.g. WETH for aWETH)
   * @param incentivesController The smart contract managing potential incentives distribution
   * @param debtTokenDecimals The decimals of the debtToken, same as the underlying asset's
   * @param debtTokenName The name of the token
   * @param debtTokenSymbol The symbol of the token
   */
  function initialize(
    ILendingPool pool,
    address underlyingAsset,
    IAaveIncentivesController incentivesController,
    uint8 debtTokenDecimals,
    string memory debtTokenName,
    string memory debtTokenSymbol,
    bytes calldata params
  ) public override initializer {
    _setName(debtTokenName);
    _setSymbol(debtTokenSymbol);
    _setDecimals(debtTokenDecimals);

    _pool = pool;
    _underlyingAsset = underlyingAsset;
    _incentivesController = incentivesController;

    emit Initialized(
      underlyingAsset,
      address(pool),
      address(incentivesController),
      debtTokenDecimals,
      debtTokenName,
      debtTokenSymbol,
      params
    );
  }

  /**
   * @dev Gets the revision of the stable debt token implementation
   * @return The debt token implementation revision
   **/
  function getRevision() internal pure virtual override returns (uint256) {
    return DEBT_TOKEN_REVISION;
  }

  /**
   * @dev Calculates the accumulated debt balance of the user
   * @return The debt balance of the user
   **/
  function balanceOf(address user) public view virtual override returns (uint256) {
    uint256 scaledBalance = super.balanceOf(user);

    if (scaledBalance == 0) {
      return 0;
    }

    return scaledBalance.rayMul(_pool.getReserveNormalizedVariableDebt(_underlyingAsset));
  }

  /**
   * @dev Mints debt token to the `onBehalfOf` address
   * -  Only callable by the LendingPool
   * @param user The address receiving the borrowed underlying, being the delegatee in case
   * of credit delegate, or same as `onBehalfOf` otherwise
   * @param onBehalfOf The address receiving the debt tokens
   * @param amount The amount of debt being minted
   * @param index The variable debt index of the reserve
   * @return `true` if the the previous balance of the user is 0
   **/
  function mint(
    address user,
    address onBehalfOf,
    uint256 amount,
    uint256 index
  ) external override onlyLendingPool returns (bool) {
    if (user != onBehalfOf) {
      _decreaseBorrowAllowance(onBehalfOf, user, amount);
    }

    uint256 previousBalance = super.balanceOf(onBehalfOf);
    uint256 amountScaled = amount.rayDiv(index);
    require(amountScaled != 0, Errors.CT_INVALID_MINT_AMOUNT);

    _mint(onBehalfOf, amountScaled);

    emit Transfer(address(0), onBehalfOf, amount);
    emit Mint(user, onBehalfOf, amount, index);

    return previousBalance == 0;
  }

  /**
   * @dev Burns user variable debt
   * - Only callable by the LendingPool
   * @param user The user whose debt is getting burned
   * @param amount The amount getting burned
   * @param index The variable debt index of the reserve
   **/
  function burn(
    address user,
    uint256 amount,
    uint256 index
  ) external override onlyLendingPool {
    uint256 amountScaled = amount.rayDiv(index);
    require(amountScaled != 0, Errors.CT_INVALID_BURN_AMOUNT);

    _burn(user, amountScaled);

    emit Transfer(user, address(0), amount);
    emit Burn(user, amount, index);
  }

  /**
   * @dev Returns the principal debt balance of the user from
   * @return The debt balance of the user since the last burn/mint action
   **/
  function scaledBalanceOf(address user) public view virtual override returns (uint256) {
    return super.balanceOf(user);
  }

  /**
   * @dev Returns the total supply of the variable debt token. Represents the total debt accrued by the users
   * @return The total supply
   **/
  function totalSupply() public view virtual override returns (uint256) {
    return super.totalSupply().rayMul(_pool.getReserveNormalizedVariableDebt(_underlyingAsset));
  }

  /**
   * @dev Returns the scaled total supply of the variable debt token. Represents sum(debt/index)
   * @return the scaled total supply
   **/
  function scaledTotalSupply() public view virtual override returns (uint256) {
    return super.totalSupply();
  }

  /**
   * @dev Returns the principal balance of the user and principal total supply.
   * @param user The address of the user
   * @return The principal balance of the user
   * @return The principal total supply
   **/
  function getScaledUserBalanceAndSupply(address user)
    external
    view
    override
    returns (uint256, uint256)
  {
    return (super.balanceOf(user), super.totalSupply());
  }

  /**
   * @dev Returns the address of the underlying asset of this aToken (E.g. WETH for aWETH)
   **/
  function UNDERLYING_ASSET_ADDRESS() public view returns (address) {
    return _underlyingAsset;
  }

  /**
   * @dev Returns the address of the incentives controller contract
   **/
  function getIncentivesController() external view override returns (IAaveIncentivesController) {
    return _getIncentivesController();
  }

  /**
   * @dev Returns the address of the lending pool where this aToken is used
   **/
  function POOL() public view returns (ILendingPool) {
    return _pool;
  }

  function _getIncentivesController() internal view override returns (IAaveIncentivesController) {
    return _incentivesController;
  }

  function _getUnderlyingAssetAddress() internal view override returns (address) {
    return _underlyingAsset;
  }

  function _getLendingPool() internal view override returns (ILendingPool) {
    return _pool;
  }
}

contracts/staking/ChefIncentivesControll.content:
// SPDX-License-Identifier: MIT
pragma solidity 0.7.6;

import "../interfaces/IMultiFeeDistribution.sol";
import "../interfaces/IEligibilityDataProvider.sol";
import "../interfaces/IOnwardIncentivesController.sol";
import "../interfaces/IAToken.sol";
import "../interfaces/IMiddleFeeDistribution.sol";
import "../interfaces/IBounty.sol";

import "../dependencies/openzeppelin/contracts/SafeMath.sol";
import "../dependencies/openzeppelin/contracts/IERC20.sol";
import "../dependencies/openzeppelin/contracts/IERC20Detailed.sol";
import "../dependencies/openzeppelin/contracts/SafeERC20.sol";
import "../dependencies/openzeppelin/upgradeability/Initializable.sol";
import "../dependencies/openzeppelin/upgradeability/OwnableUpgradeable.sol";
import "../dependencies/openzeppelin/upgradeability/PausableUpgradeable.sol";

// based on the Sushi MasterChef
// https://github.com/sushiswap/sushiswap/blob/master/contracts/MasterChef.sol
contract ChefIncentivesController is
    Initializable,
    PausableUpgradeable,
    OwnableUpgradeable
{
    using SafeMath for uint256;
    using SafeERC20 for IERC20;

    // Info of each user.
    // reward = user.`amount` * pool.`accRewardPerShare` - `rewardDebt`
    struct UserInfo {
        uint256 amount;
        uint256 rewardDebt;
        uint256 enterTime;
        uint256 lastClaimTime;
    }

    // Info of each pool.
    struct PoolInfo {
        uint256 totalSupply;
        uint256 allocPoint; // How many allocation points assigned to this pool.
        uint256 lastRewardTime; // Last second that reward distribution occurs.
        uint256 accRewardPerShare; // Accumulated rewards per share, times ACC_REWARD_PRECISION. See below.
        IOnwardIncentivesController onwardIncentives;
    }

    // Info about token emissions for a given time period.
    struct EmissionPoint {
        uint128 startTimeOffset;
        uint128 rewardsPerSecond;
    }

    // Emitted when rewardPerSecond is updated
    event RewardsPerSecondUpdated(
        uint256 indexed rewardsPerSecond,
        bool persist
    );

    event BalanceUpdated(
        address indexed token,
        address indexed user,
        uint256 balance,
        uint256 totalSupply
    );

    event EmissionScheduleAppended(
        uint256[] startTimeOffsets,
        uint256[] rewardsPerSeconds
    );

    event ChefReserveLow(uint256 _balance);

    event ChefReserveEmpty(uint256 _balance);

    event Disqualified(address indexed user, uint256 rewardsRemoved);

    // multiplier for reward calc
    uint256 private constant ACC_REWARD_PRECISION = 1e12;

    // Data about the future reward rates. emissionSchedule stored in chronological order,
    // whenever the number of blocks since the start block exceeds the next block offset a new
    // reward rate is applied.
    EmissionPoint[] public emissionSchedule;

    // If true, keep this new reward rate indefinitely
    // If false, keep this reward rate until the next scheduled block offset, then return to the schedule.
    bool public persistRewardsPerSecond;

    /********************** Emission Info ***********************/

    // Array of tokens for reward
    address[] public registeredTokens;

    // Current reward per second
    uint256 public rewardsPerSecond;

    // last RPS, used during refill after reserve empty
    uint256 public lastRPS;

    // Index in emission schedule which the last rewardsPerSeconds was used
    // only used for scheduled rewards
    uint256 public emissionScheduleIndex;

    // Info of each pool.
    mapping(address => PoolInfo) public poolInfo;
    mapping(address => bool) private validRTokens;

    // Total allocation poitns. Must be the sum of all allocation points in all pools.
    uint256 public totalAllocPoint;

    // token => user => Info of each user that stakes LP tokens.
    mapping(address => mapping(address => UserInfo)) public userInfo;

    // user => base claimable balance
    mapping(address => uint256) public userBaseClaimable;

    // MFD, LPMFD, bounties, AC, middlefee
    mapping(address => bool) public eligibilityExempt;

    // The block number when reward mining starts.
    uint256 public startTime;

    bool public ELIGIBILITY_ENABLED;

    address public poolConfigurator;
    uint256 public depositedRewards;
    uint256 public accountedRewards;
    uint256 public lastAllPoolUpdate;

    IMiddleFeeDistribution public rewardMinter;
    IEligibilityDataProvider public eligibleDataProvider;
    address public bountyManager;

    function initialize(
        address _poolConfigurator,
        IEligibilityDataProvider _eligibleDataProvider,
        IMiddleFeeDistribution _rewardMinter,
        uint256 _rewardsPerSecond
    ) public initializer {
        require(address(_poolConfigurator) != address(0));
        require(address(_eligibleDataProvider) != address(0));
        require(address(_rewardMinter) != address(0));

        __Ownable_init();
        __Pausable_init();

        poolConfigurator = _poolConfigurator;
        eligibleDataProvider = _eligibleDataProvider;
        rewardMinter = _rewardMinter;
        rewardsPerSecond = _rewardsPerSecond;
        persistRewardsPerSecond = true;

        ELIGIBILITY_ENABLED = true;
    }

    function poolLength() external view returns (uint256) {
        return registeredTokens.length;
    }

    function _getMfd() internal view returns (IMultiFeeDistribution mfd) {
        address multiFeeDistribution = rewardMinter
            .getMultiFeeDistributionAddress();
        mfd = IMultiFeeDistribution(multiFeeDistribution);
    }

    function _getLpMfd() internal view returns (IMultiFeeDistribution mfd) {
        address multiFeeDistribution = rewardMinter
            .getLPFeeDistributionAddress();
        mfd = IMultiFeeDistribution(multiFeeDistribution);
    }

    function setOnwardIncentives(
        address _token,
        IOnwardIncentivesController _incentives
    ) external onlyOwner {
        require(poolInfo[_token].lastRewardTime != 0, "pool doesn't exist");
        poolInfo[_token].onwardIncentives = _incentives;
    }

    function setBountyManager(address _bountyManager) external onlyOwner {
        bountyManager = _bountyManager;
    }

    function setEligibilityEnabled(bool _newVal) external onlyOwner {
        ELIGIBILITY_ENABLED = _newVal;
    }

    /********************** Pool Setup + Admin ***********************/

    function start() public onlyOwner {
        require(startTime == 0, "already started");
        startTime = block.timestamp;
    }

    // Add a new lp to the pool. Can only be called by the poolConfigurator.
    function addPool(address _token, uint256 _allocPoint) external {
        require(msg.sender == poolConfigurator, "not allowed");
        require(poolInfo[_token].lastRewardTime == 0, "pool already exists");
        _updateEmissions();
        totalAllocPoint = totalAllocPoint.add(_allocPoint);
        registeredTokens.push(_token);
        poolInfo[_token] = PoolInfo({
            totalSupply: 0,
            allocPoint: _allocPoint,
            lastRewardTime: block.timestamp,
            accRewardPerShare: 0,
            onwardIncentives: IOnwardIncentivesController(0)
        });
        validRTokens[_token] = true;
    }

    // Update the given pool's allocation point. Can only be called by the owner.
    function batchUpdateAllocPoint(
        address[] calldata _tokens,
        uint256[] calldata _allocPoints
    ) public onlyOwner {
        require(
            _tokens.length == _allocPoints.length,
            "params length mismatch"
        );
        _massUpdatePools();
        uint256 _totalAllocPoint = totalAllocPoint;
        for (uint256 i = 0; i < _tokens.length; i++) {
            PoolInfo storage pool = poolInfo[_tokens[i]];
            require(pool.lastRewardTime > 0, "pool doesn't exist");
            _totalAllocPoint = _totalAllocPoint.sub(pool.allocPoint).add(
                _allocPoints[i]
            );
            pool.allocPoint = _allocPoints[i];
        }
        totalAllocPoint = _totalAllocPoint;
    }

    /**
     * @notice Sets the reward per second to be distributed. Can only be called by the owner.
     * @dev Its decimals count is ACC_REWARD_PRECISION
     * @param _rewardsPerSecond The amount of reward to be distributed per second.
     */
    function setRewardsPerSecond(uint256 _rewardsPerSecond, bool _persist)
        external
        onlyOwner
    {
        _massUpdatePools();
        rewardsPerSecond = _rewardsPerSecond;
        persistRewardsPerSecond = _persist;
        emit RewardsPerSecondUpdated(_rewardsPerSecond, _persist);
    }

    function setScheduledRewardsPerSecond() internal {
        if (!persistRewardsPerSecond) {
            uint256 length = emissionSchedule.length;
            uint256 i = emissionScheduleIndex;
            uint128 offset = uint128(block.timestamp.sub(startTime));
            for (
                ;
                i < length && offset >= emissionSchedule[i].startTimeOffset;
                i++
            ) {}
            if (i > emissionScheduleIndex) {
                emissionScheduleIndex = i;
                _massUpdatePools();
                rewardsPerSecond = uint256(
                    emissionSchedule[i - 1].rewardsPerSecond
                );
            }
        }
    }

    function setEmissionSchedule(
        uint256[] calldata _startTimeOffsets,
        uint256[] calldata _rewardsPerSecond
    ) external onlyOwner {
        uint256 length = _startTimeOffsets.length;
        require(
            length > 0 && length == _rewardsPerSecond.length,
            "empty or mismatch params"
        );

        for (uint256 i = 0; i < length; i++) {
            require(
                _startTimeOffsets[i] <= type(uint128).max,
                "startTimeOffsets > max uint128"
            );
            require(
                _rewardsPerSecond[i] <= type(uint128).max,
                "rewardsPerSecond > max uint128"
            );

            if (startTime > 0) {
                require(
                    _startTimeOffsets[i] > block.timestamp.sub(startTime),
                    "invalid start time"
                );
            }
            emissionSchedule.push(
                EmissionPoint({
                    startTimeOffset: uint128(_startTimeOffsets[i]),
                    rewardsPerSecond: uint128(_rewardsPerSecond[i])
                })
            );
        }
        emit EmissionScheduleAppended(_startTimeOffsets, _rewardsPerSecond);
    }

    function recoverERC20(address tokenAddress, uint256 tokenAmount)
        external
        onlyOwner
    {
        IERC20(tokenAddress).safeTransfer(owner(), tokenAmount);
    }

    /********************** Pool State Changers ***********************/

    function _updateEmissions() internal {
        if (block.timestamp > endRewardTime()) {
            _massUpdatePools();
            lastRPS = rewardsPerSecond;
            rewardsPerSecond = 0;
            return;
        }
        setScheduledRewardsPerSecond();
    }

    // Update reward variables for all pools
    function _massUpdatePools() internal {
        uint256 totalAP = totalAllocPoint;
        uint256 length = registeredTokens.length;
        for (uint256 i = 0; i < length; ++i) {
            _updatePool(poolInfo[registeredTokens[i]], totalAP);
        }
        lastAllPoolUpdate = block.timestamp;
    }

    // Update reward variables of the given pool to be up-to-date.
    function _updatePool(PoolInfo storage pool, uint256 _totalAllocPoint)
        internal
    {
        uint256 timestamp = block.timestamp;
        if (endRewardTime() <= block.timestamp) {
            timestamp = endRewardTime();
        }
        if (timestamp <= pool.lastRewardTime) {
            return;
        }

        uint256 lpSupply = pool.totalSupply;
        if (lpSupply == 0) {
            pool.lastRewardTime = timestamp;
            return;
        }

        uint256 duration = timestamp.sub(pool.lastRewardTime);
        uint256 rawReward = duration.mul(rewardsPerSecond);
        if (availableRewards() < rawReward) {
            rawReward = availableRewards();
        }
        uint256 reward = rawReward.mul(pool.allocPoint).div(_totalAllocPoint);
        accountedRewards = accountedRewards.add(reward);
        pool.accRewardPerShare = pool.accRewardPerShare.add(
            reward.mul(ACC_REWARD_PRECISION).div(lpSupply)
        );
        pool.lastRewardTime = timestamp;
    }

    /********************** Emission Calc + Transfer ***********************/

    function pendingRewards(address _user, address[] memory _tokens)
        public
        view
        returns (uint256[] memory)
    {
        uint256[] memory claimable = new uint256[](_tokens.length);
        for (uint256 i = 0; i < _tokens.length; i++) {
            address token = _tokens[i];
            PoolInfo storage pool = poolInfo[token];
            UserInfo storage user = userInfo[token][_user];
            uint256 accRewardPerShare = pool.accRewardPerShare;
            uint256 lpSupply = pool.totalSupply;
            if (block.timestamp > pool.lastRewardTime && lpSupply != 0) {
                uint256 duration = block.timestamp.sub(pool.lastRewardTime);
                uint256 reward = duration
                    .mul(rewardsPerSecond)
                    .mul(pool.allocPoint)
                    .div(totalAllocPoint);
                accRewardPerShare = accRewardPerShare.add(
                    reward.mul(ACC_REWARD_PRECISION).div(lpSupply)
                );
            }
            claimable[i] = user
                .amount
                .mul(accRewardPerShare)
                .div(ACC_REWARD_PRECISION)
                .sub(user.rewardDebt);
        }
        return claimable;
    }

    // Claim pending rewards for one or more pools.
    // Rewards are not received directly, they are minted by the rewardMinter.
    function claim(address _user, address[] memory _tokens) public {
        if (ELIGIBILITY_ENABLED) {
            checkAndProcessEligibility(_user);
        }

        _updateEmissions();

        uint256 pending = userBaseClaimable[_user];
        userBaseClaimable[_user] = 0;
        uint256 _totalAllocPoint = totalAllocPoint;
        for (uint256 i = 0; i < _tokens.length; i++) {
            require(validRTokens[_tokens[i]]);
            PoolInfo storage pool = poolInfo[_tokens[i]];
            require(pool.lastRewardTime > 0, "pool doesn't exist");
            _updatePool(pool, _totalAllocPoint);
            UserInfo storage user = userInfo[_tokens[i]][_user];
            uint256 rewardDebt = user.amount.mul(pool.accRewardPerShare).div(
                ACC_REWARD_PRECISION
            );
            pending = pending.add(rewardDebt.sub(user.rewardDebt));
            user.rewardDebt = rewardDebt;
            user.lastClaimTime = block.timestamp;
        }

        _mint(_user, pending);

        if (endRewardTime() < block.timestamp + 5 days) {
            _emitReserveLow();
        }
    }

    function _emitReserveLow() internal {
        address rdntToken = rewardMinter.getRdntTokenAddress();
        emit ChefReserveLow(IERC20(rdntToken).balanceOf(address(this)));
    }

    function _mint(address _user, uint256 _amount) internal {
        _amount = _sendRadiant(address(_getMfd()), _amount);
        _getMfd().mint(_user, _amount, true);
    }

    function setEligibilityExempt(address _contract) public onlyOwner {
        eligibilityExempt[_contract] = true;
    }

    /********************** Eligibility + Disqualification ***********************/

    /**
     * @notice `after` Hook for deposit and borrow update.
     * @dev important! eligible status can be updated here
     */
    function handleActionAfter(
        address _user,
        uint256 _balance,
        uint256 _totalSupply
    ) external {
        require(
            validRTokens[msg.sender] || msg.sender == address(_getLpMfd()),
            "!rToken || lpmfd"
        );

        if (
            _user == address(rewardMinter) ||
            _user == address(_getMfd()) ||
            _user == address(_getLpMfd()) ||
            eligibilityExempt[_user]
        ) {
            return;
        }
        if (ELIGIBILITY_ENABLED) {
            eligibleDataProvider.refresh(_user);
            if (eligibleDataProvider.isEligibleForRewards(_user)) {
                _handleActionAfterForToken(
                    msg.sender,
                    _user,
                    _balance,
                    _totalSupply
                );
            } else {
                checkAndProcessEligibility(_user);
            }
        } else {
            _handleActionAfterForToken(
                msg.sender,
                _user,
                _balance,
                _totalSupply
            );
        }
    }

    function _handleActionAfterForToken(
        address _token,
        address _user,
        uint256 _balance,
        uint256 _totalSupply
    ) internal {
        PoolInfo storage pool = poolInfo[_token];
        require(pool.lastRewardTime > 0, "pool doesn't exist");
        _updateEmissions();
        _updatePool(pool, totalAllocPoint);
        UserInfo storage user = userInfo[_token][_user];
        uint256 amount = user.amount;
        uint256 accRewardPerShare = pool.accRewardPerShare;
        if (amount != 0) {
            uint256 pending = amount
                .mul(accRewardPerShare)
                .div(ACC_REWARD_PRECISION)
                .sub(user.rewardDebt);
            if (pending != 0) {
                userBaseClaimable[_user] = userBaseClaimable[_user].add(
                    pending
                );
            }
        }
        pool.totalSupply = pool.totalSupply.sub(user.amount);
        user.amount = _balance;
        user.rewardDebt = _balance.mul(accRewardPerShare).div(
            ACC_REWARD_PRECISION
        );
        if (user.amount > 0) {
            user.enterTime = block.timestamp;
        }
        pool.totalSupply = pool.totalSupply.add(_balance);
        if (pool.onwardIncentives != IOnwardIncentivesController(0)) {
            pool.onwardIncentives.handleAction(
                _token,
                _user,
                _balance,
                _totalSupply
            );
        }

        emit BalanceUpdated(_token, _user, _balance, _totalSupply);
    }

    /**
     * @notice `before` Hook for deposit and borrow update.
     */
    function handleActionBefore(address _user) external {}

    /**
     * @notice Hook for lock update.
     * @dev Called by the locking contracts before locking or unlocking happens
     */
    function beforeLockUpdate(address _user) external {
        require(
            msg.sender == address(_getLpMfd()) ||
                msg.sender == address(_getMfd())
        );
        if (ELIGIBILITY_ENABLED) {
            uint256 userBounty = bountyForUser(_user);
            bool isRelock = !_getLpMfd().autoRelockDisabled(_user);
            if (userBounty != 0 && !isRelock) {
                checkAndProcessEligibility(_user);
            }
        }
    }

    /**
     * @notice Hook for lock update.
     * @dev Called by the locking contracts after locking or unlocking happens
     */
    function afterLockUpdate(address _user) external {
        require(
            msg.sender == address(_getLpMfd()) ||
                msg.sender == address(_getMfd()),
            "!lpMFD || !MFD"
        );

        if (ELIGIBILITY_ENABLED) {
            eligibleDataProvider.updatePrice();
            if (eligibleDataProvider.isEligibleForRewards(_user)) {
                for (uint256 i = 0; i < registeredTokens.length; i++) {
                    uint256 newBal = IERC20(registeredTokens[i]).balanceOf(
                        _user
                    );
                    if (newBal != 0) {
                        _handleActionAfterForToken(
                            registeredTokens[i],
                            _user,
                            newBal,
                            poolInfo[registeredTokens[i]]
                                .totalSupply
                                .add(newBal)
                                .sub(
                                    userInfo[registeredTokens[i]][_user].amount
                                )
                        );
                    }
                }
            }
            eligibleDataProvider.refresh(_user);
        }
    }

    /********************** Eligibility + Disqualification ***********************/

    function earnedSince(address _user, uint256 lastEligibleTime)
        public
        view
        returns (uint256 earnedAmount)
    {
        if (
            !ELIGIBILITY_ENABLED ||
            eligibleDataProvider.isEligibleForRewards(_user)
        ) {
            return 0;
        }

        uint256 ineligibleDuration;
        if (lastEligibleTime < block.timestamp) {
            ineligibleDuration = block.timestamp.sub(lastEligibleTime);
        }

        uint256[] memory claimable = pendingRewards(_user, registeredTokens);
        for (uint256 i = 0; i < claimable.length; i++) {
            UserInfo storage user = userInfo[registeredTokens[i]][_user];
            uint256 referenceTime;

            if (user.lastClaimTime > user.enterTime) {
                referenceTime = user.lastClaimTime;
            } else {
                referenceTime = user.enterTime;
            }

            uint256 referenceDuration = block.timestamp - referenceTime;
            if (referenceDuration > 0) {
                uint256 rps = claimable[i].div(referenceDuration);
                uint256 ineligAmt = ineligibleDuration.mul(rps);
                if (ineligAmt > claimable[i]) {
                    ineligAmt = claimable[i];
                }
                earnedAmount = earnedAmount.add(ineligAmt);
            }
        }
    }

    function bountyForUser(address _user) public view returns (uint256 bounty) {
        bounty = earnedSince(
            _user,
            eligibleDataProvider.lastEligibleTime(_user)
        );
    }

    function harvestIneligible(address _target, uint256 _bounty)
        internal
        returns (uint256)
    {
        require(ELIGIBILITY_ENABLED, "!EE");
        claimToBase(_target, registeredTokens);
        if (_bounty < userBaseClaimable[_target]) {
            userBaseClaimable[_target] = userBaseClaimable[_target].sub(
                _bounty
            );
        } else {
            _bounty = userBaseClaimable[_target];
            userBaseClaimable[_target] = 0;
        }
        return _bounty;
    }

    function hasEligibleDeposits(address _user)
        internal
        view
        returns (bool hasDeposits)
    {
        for (uint256 i = 0; i < registeredTokens.length; i++) {
            UserInfo storage user = userInfo[registeredTokens[i]][_user];
            if (user.amount != 0) {
                hasDeposits = true;
                break;
            }
        }
    }

    function checkAndProcessEligibility(address _user, bool _execute)
        internal
        returns (uint256 bountyAmt, bool issueBaseBounty)
    {
        // for expire DQ
        bool hasRelock = !_getLpMfd().autoRelockDisabled(_user);
        bool isMarketDq = eligibleDataProvider.isMarketDisqualified(_user);
        bool isEligible = eligibleDataProvider.isEligibleForRewards(_user);
        uint256 lastEligibleTime = eligibleDataProvider.lastEligibleTime(_user);
        uint256 lastDqTime = eligibleDataProvider.getDqTime(_user);
        bool hasEligDeposits = hasEligibleDeposits(_user);
        bool alreadyDqd = lastDqTime != 0;

        // market dq:
        //    stop all types immediately, BB
        // timedq:
        //    if !relock, remove inelig
        //    if relock, no-op
        if (!isEligible && !alreadyDqd) {
            // inelig earned emissions
            if (isMarketDq && hasEligDeposits) {
                // all user types DQ when market, will DQ below if _execute
                issueBaseBounty = true;
            } else {
                // expired dq
                if (
                    lastEligibleTime != 0 && lastEligibleTime < block.timestamp
                ) {
                    if (!hasRelock) {
                        bountyAmt = bountyForUser(_user);
                    }
                }
            }
        }
        if (_execute) {
            if (bountyAmt != 0 || issueBaseBounty) {
                require(!isEligible, "user still eligible");
                stopEmissionsFor(_user);
            }

            if (bountyAmt != 0) {
                uint256 rewardsRemoved = harvestIneligible(_user, bountyAmt);
                emit Disqualified(_user, rewardsRemoved);
            }
            eligibleDataProvider.refresh(_user);
        }
    }

    function checkAndProcessEligibility(address _user) internal {
        checkAndProcessEligibility(_user, true);
    }

    function claimBounty(address _user, bool _execute)
        public
        returns (uint256 bountyAmt, bool issueBaseBounty)
    {
        require(msg.sender == address(bountyManager), "bounty only");
        (bountyAmt, issueBaseBounty) = checkAndProcessEligibility(
            _user,
            _execute
        );
        if (_execute) {
            address rdntAddr = rewardMinter.getRdntTokenAddress();
            IERC20(rdntAddr).safeApprove(address(bountyManager), bountyAmt);
        }
    }

    function stopEmissionsFor(address _user) internal {
        require(ELIGIBILITY_ENABLED, "!EE");
        require(
            !eligibleDataProvider.isEligibleForRewards(_user),
            "user is still eligible"
        );
        uint256 length = registeredTokens.length;
        for (uint256 i = 0; i < length; ++i) {
            address token = registeredTokens[i];
            PoolInfo storage pool = poolInfo[token];
            UserInfo storage user = userInfo[token][_user];

            _handleActionAfterForToken(
                token,
                _user,
                0,
                pool.totalSupply.sub(user.amount)
            );
        }
        eligibleDataProvider.setDqTime(_user, block.timestamp);
    }

    function _sendRadiant(address _user, uint256 _amount)
        internal
        returns (uint256)
    {
        if (_amount == 0) {
            return 0;
        }

        address rdntToken = rewardMinter.getRdntTokenAddress();
        uint256 chefReserve = IERC20(rdntToken).balanceOf(address(this));
        if (_amount > chefReserve) {
            emit ChefReserveEmpty(chefReserve);
            _pause();
        } else {
            IERC20(rdntToken).safeTransfer(_user, _amount);
        }
        return _amount;
    }

    /********************** RDNT Reserve Management ***********************/

    function endRewardTime() public view returns (uint256 timestamp) {
        uint256 unclaimedRewards = depositedRewards.sub(accountedRewards);
        uint256 extra = 0;
        for (uint256 i; i < registeredTokens.length; i++) {
            if (
                poolInfo[registeredTokens[i]].lastRewardTime <=
                lastAllPoolUpdate
            ) {
                continue;
            } else {
                extra = extra.add(
                    poolInfo[registeredTokens[i]]
                        .lastRewardTime
                        .sub(lastAllPoolUpdate)
                        .mul(poolInfo[registeredTokens[i]].allocPoint)
                        .mul(rewardsPerSecond)
                        .div(totalAllocPoint)
                );
            }
        }
        if (rewardsPerSecond == 0) {
            timestamp = uint256(-1);
        } else {
            timestamp =
                (unclaimedRewards + extra).div(rewardsPerSecond) +
                (lastAllPoolUpdate);
        }
    }

    function registerRewardDeposit(uint256 _amount) external onlyOwner {
        depositedRewards = depositedRewards.add(_amount);
        _massUpdatePools();
        if (rewardsPerSecond == 0 && lastRPS > 0) {
            rewardsPerSecond = lastRPS;
        }
    }

    function availableRewards() internal view returns (uint256 amount) {
        return depositedRewards.sub(accountedRewards);
    }

    /********************** Helper/Convenience Methods ***********************/

    /**
     * @notice Claim pending rewards for one or more pools into base claimable.
     * @dev Rewards are not transferred, just converted into base claimable.
     */
    function claimToBase(address _user, address[] memory _tokens) public {
        uint256 _userBaseClaimable = userBaseClaimable[_user];

        // updatePool must be called after calculation of pending rewards
        // this is because of reward calculation based on eligibility
        uint256[] memory pending = pendingRewards(_user, _tokens);
        _updateEmissions();
        uint256 _totalAllocPoint = totalAllocPoint;
        for (uint256 i = 0; i < _tokens.length; i++) {
            require(validRTokens[_tokens[i]]);
            UserInfo storage user = userInfo[_tokens[i]][_user];
            _userBaseClaimable = _userBaseClaimable.add(pending[i]);

            // Set pending reward to zero
            PoolInfo storage pool = poolInfo[_tokens[i]];
            _updatePool(pool, _totalAllocPoint);
            uint256 newDebt = user.amount.mul(pool.accRewardPerShare).div(
                ACC_REWARD_PRECISION
            );
            user.rewardDebt = newDebt;
            user.lastClaimTime = block.timestamp;
        }
        userBaseClaimable[_user] = _userBaseClaimable;
    }

    function saveUserRewards(address[] memory _users) public {
        address[] memory _tokens = registeredTokens;
        for (uint256 i = 0; i < _users.length; i++) {
            if (_users[i] != address(0)) {
                claimToBase(_users[i], _tokens);
            }
        }
    }

    function claimAll(address _user) external {
        claim(_user, registeredTokens);
    }

    function allPendingRewards(address _user)
        public
        view
        returns (uint256 pending)
    {
        pending = userBaseClaimable[_user];
        uint256[] memory claimable = pendingRewards(_user, registeredTokens);
        for (uint256 i = 0; i < claimable.length; i++) {
            pending += claimable[i];
        }
    }
}

contracts/staking/LockerList.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity 0.7.6;
pragma abicoder v2;

import "../libraries/AddressPagination.sol";
import "../dependencies/openzeppelin/contracts/Ownable.sol";


/// @title Locker List Contract
/// @author Radiant
/// @dev All function calls are currently implemented without side effects
contract LockerList is Ownable {
    using AddressPagination for address[];

    // Users list
    address[] internal userlist;
    mapping(address => uint256) internal indexOf;
    mapping(address => bool) internal inserted;

    /********************** Events ***********************/

    event LockerAdded(address indexed locker);
    event LockerRemoved(address indexed locker);

    /**
     * @dev Constructor
     */
    constructor() Ownable() {}

    /********************** Lockers list ***********************/
    /**
     * @notice Return the number of users.
     */
    function lockersCount() external view returns (uint256) {
        return userlist.length;
    }

    /**
     * @notice Return the list of users.
     */
    function getUsers(uint256 page, uint256 limit)
        external
        view
        returns (address[] memory)
    {
        return userlist.paginate(page, limit);
    }

    function addToList(address user) external onlyOwner {
        if (inserted[user] == false) {
            inserted[user] = true;
            indexOf[user] = userlist.length;
            userlist.push(user);
        }

        emit LockerAdded(user);
    }

    function removeFromList(address user) external onlyOwner {
        assert(inserted[user] == true);

        delete inserted[user];

        uint256 index = indexOf[user];
        uint256 lastIndex = userlist.length - 1;
        address lastUser = userlist[lastIndex];

        indexOf[lastUser] = index;
        delete indexOf[user];

        userlist[index] = lastUser;
        userlist.pop();

        emit LockerRemoved(user);
    }
}

contracts/staking/MFDHelper.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity 0.7.6;
pragma abicoder v2;

import "../interfaces/IAToken.sol";
import "../interfaces/ILendingPoolAddressesProvider.sol";
import "../interfaces/ILendingPool.sol";
import "../interfaces/IFeeDistribution.sol";
import "../interfaces/IMultiFeeDistribution.sol";
import "../dependencies/openzeppelin/upgradeability/Initializable.sol";
import "../dependencies/openzeppelin/upgradeability/OwnableUpgradeable.sol";
import "../dependencies/openzeppelin/contracts/SafeMath.sol";

/*
 * Helper functions for MFD compounding + bounties
 */
contract MFDHelper is Initializable, OwnableUpgradeable {
    using SafeMath for uint256;

    event RoutesUpdated(
        address _token,
        address[] _routes
    );
    event RewardBaseTokensUpdated(
        address[] _tokens
    );

    mapping(address => address[]) public rewardToBaseRoute;
    address public baseToken; // weth
    address public rdnt;
    address public addressProvider;
    address[] public rewardBaseTokens;
    address public mfd;

    function initialize(
        address _baseToken,
        address _rdnt,
        address _addressProvider

    ) public initializer {
        __Ownable_init();

        baseToken = _baseToken;
        rdnt = _rdnt;
        addressProvider = _addressProvider;
    }

    function addRewardBaseTokens(address[] memory _tokens) external onlyOwner{
        rewardBaseTokens = _tokens;
        emit RewardBaseTokensUpdated(_tokens);
    }

    function setRoutes(address _token, address[] memory _routes) external onlyOwner{
        rewardToBaseRoute[_token] = _routes;
        emit RoutesUpdated(_token, _routes);
    }

    /**
     * @notice Bounty amount of user per each reward token
     * @dev Reward is RDNT and rTokens
     */
    function getIneligibleRewards(
        IFeeDistribution.RewardData[] memory pendingRewards,
        LockedBalance[] memory locks,
        uint256 lastClaimTime
    )
        external
        view
        returns (IFeeDistribution.RewardData[] memory bounties)
    {
        uint256 percentOver = _getIneligiblePercent(locks, lastClaimTime);

        bounties = new IFeeDistribution.RewardData[](
            pendingRewards.length
        );

        for (uint256 i = 0; i < pendingRewards.length; i++) {
            address token = pendingRewards[i].token;
            uint256 amount = pendingRewards[i].amount;

            bounties[i].token = token;
            // > 100% ineligible time
            if (percentOver > 10000) {
                bounties[i].amount = amount;
            } else {
                bounties[i].amount = amount.mul(percentOver).div(10000);
            }
        }
    }

    /**
     * @notice Decide ineligble percent of the user
     */
    function _getIneligiblePercent(
        LockedBalance[] memory locks,
        uint256 lastClaimTime
    )
        internal
        view
        returns (uint256 percentOver)
    {
        uint256 totalLockAMTxTIME;
        uint256 expiredAMTxTIME;

        for (uint256 i = 0; i < locks.length; i++) {
            uint256 startTime = lastClaimTime != 0
                ? lastClaimTime
                : locks[i].unlockTime.sub(
                    locks[i].duration
                );
            totalLockAMTxTIME += locks[i].amount.mul(
                block.timestamp - startTime
            );
            if (locks[i].unlockTime < block.timestamp) {
                uint256 timeDiff = block.timestamp.sub(
                    locks[i].unlockTime
                );
                expiredAMTxTIME += timeDiff.mul(locks[i].amount);
            }
        }
        if (totalLockAMTxTIME != 0) {
            percentOver = expiredAMTxTIME.mul(10000).div(totalLockAMTxTIME);
        }
    }

    function getLendingPool() external view returns (address) {
        return ILendingPoolAddressesProvider(addressProvider).getLendingPool();
    }

    function getUnderlying(address token) external view returns (address underlying) {
        underlying = IAToken(token).UNDERLYING_ASSET_ADDRESS();
    }

    function getRewardToBaseRoute(address token) external view returns (address[] memory) {
        return rewardToBaseRoute[token];
    }

    function getRouteToRdnt() external view returns (address[] memory routeToRdnt) {
        routeToRdnt = new address[](2);
        routeToRdnt[0] = baseToken;
        routeToRdnt[1] = address(rdnt);
        return routeToRdnt;
    }

    function quoteSelfCompound() external view returns (uint256 tokensOut) {
        (bool success, bytes memory data) = address(mfd).staticcall(
            abi.encodeWithSignature(
                "convertPendingRewardsToWeth(address)",
                msg.sender
            )
        );
        require(success);
        tokensOut = abi.decode(data, (uint256));
    }

    function setMFD(address _mfd) external onlyOwner {
        mfd = _mfd;
    }
}

contracts/staking/MFDPlus.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity 0.7.6;
pragma abicoder v2;

import "../staking/MultiFeeDistribution.sol";
import "../uniswap/periphery/interfaces/IUniswapV2Router02.sol";
import "../interfaces/ILendingPool.sol";
import "../interfaces/IFeeDistribution.sol";
import "../interfaces/ILockZap.sol";
import "../interfaces/IMFDHelper.sol";

/// @title MFD Plus Bounty and Compund
/// @author Radiant
/// @dev All function calls are currently implemented without side effects
contract MFDPlus is MultiFeeDistribution {
	using SafeERC20 for IERC20;
	using SafeMath for uint256;
	using SafeERC20 for IMintableToken;

	// Option flags
	bool public BOUNTY_ENABLED;
	bool public AC_ENABLED;

	// note: relock disabled is default, autocompound default false
	mapping(address => bool) public autocompoundEnabled;
	mapping(address => uint256) public lastAutocompound;

	// Compound params
	uint256 public autocompoundThreshold;
	uint256 public autocompoundFee;
	uint256 public compoundAcceptableSlippage;

	// Contract Addresses
	address public bountyManager;
	address public baseToken; // weth
	IMFDHelper public mfdHelper;
	address public uniRouter;

	event Compounded(address indexed _user, uint256 _amount);
	event ExpiredLocksRemoved(address indexed _user, uint256 unlockable, uint256 ineligInRdnt);

	/************************ Setters ************************/

	function setCompoundOptions(
		address _baseToken,
		address _uniRouter,
		address _bountyManager,
		address _mfdHelper,
		bool _bountyEnabled,
		bool _acEnabled,
		uint256 _autocompoundThreshold,
		uint256 _autocompoundFee,
		uint256 _compoundAcceptableSlippage
	) public onlyOwner {
		// require(_uniRouter != address(0));
		// require(_baseToken != address(0));
		// require(_bountyManager != address(0));
		// require(_mfdHelper != address(0));
		// require(_autocompoundThreshold != uint256(0));
		// require(_autocompoundFee != uint256(0));
		// require(_compoundAcceptableSlippage != uint256(0));

		uniRouter = _uniRouter;
		baseToken = _baseToken;
		bountyManager = _bountyManager;
		mfdHelper = IMFDHelper(_mfdHelper);
		autocompoundThreshold = _autocompoundThreshold; //RDNT
		autocompoundFee = _autocompoundFee; //%
		compoundAcceptableSlippage = _compoundAcceptableSlippage;
		BOUNTY_ENABLED = _bountyEnabled;
		AC_ENABLED = _acEnabled;
	}

	// function setAutoCompoundAndBountiesEnabled(bool _AC_ENABLED, bool _BOUNTY_ENABLED) public onlyOwner {
	// 	AC_ENABLED = _AC_ENABLED;
	// 	BOUNTY_ENABLED = _BOUNTY_ENABLED;
	// }

	/**
	 * @notice set autocompound status for msg.sender
	 * @param _status AC on?
	 */
	function setAutocompound(bool _status) external {
		autocompoundEnabled[msg.sender] = _status;
	}

	/************************ Execution ************************/

	/**
	 * @notice Claims bounty.
	 * @dev Remove expired locks
	 * @param _user address.
	 * @param _execute flag for actual claim.
	 */
	function claimBounty(address _user, bool _execute) public returns (uint256 bountyAmt, bool issueBaseBounty) {
		require(BOUNTY_ENABLED);
		require(msg.sender == address(bountyManager));

		if (_execute) {
			_updateReward(_user);
		}

		(, uint256 unlockable, , , ) = lockedBalances(_user);
		if (unlockable == 0) {
			return (0, false);
		}

		// If Relock is true, then issue base bounty
		if (!autoRelockDisabled[_user]) {
			issueBaseBounty = true;
		}

		if (!_execute) {
			// If relock is false, then calc bounty
			if (autoRelockDisabled[_user]) {
				bountyAmt = _wethToRdnt(
					_convertPendingRewardsToWeth(_user, bountyForUser(_user), _execute, true),
					false
				);
			}
			// If relock is true, just issue base bounty
			return (bountyAmt, issueBaseBounty);
		} else {
			// If not relock, then remove ineleig plat rev
			if (autoRelockDisabled[_user]) {
				bountyAmt = _removeIneligiblePlatRev(_user);
			}
			// Withdraw the user's expried locks
			_withdrawExpiredLocksFor(_user, false, true, userLocks[_user].length);
			emit ExpiredLocksRemoved(_user, unlockable, bountyAmt);
			rdntToken.safeApprove(address(bountyManager), bountyAmt);
		}
	}

	/**
	 * @notice Remove inelgible Platform Revenue.
	 * @param _user address.
	 */
	function _removeIneligiblePlatRev(address _user) internal returns (uint256 ineligRemovedInRdnt) {
		require(BOUNTY_ENABLED);

		IFeeDistribution.RewardData[] memory bounties = bountyForUser(_user);
		IFeeDistribution.RewardData[] memory penalties = new IFeeDistribution.RewardData[](bounties.length);

		for (uint256 i; i < bounties.length; i++) {
			address token = bounties[i].token;
			uint256 penalty = bounties[i].amount;
			uint256 reward = rewards[_user][token].div(1e12);

			// Avoid cases if reward not enough
			if (reward > penalty) {
				reward = reward.sub(penalty);
			} else {
				penalty = reward;
				reward = 0;
			}
			rewards[_user][token] = reward.mul(1e12);

			penalties[i].token = token;
			penalties[i].amount = penalty;

			emit IneligibleRewardRemoved(_user, token, penalty);
		}

		ineligRemovedInRdnt = _wethToRdnt(_convertPendingRewardsToWeth(_user, penalties, true, false), true);
	}

	/**
	 * @notice Bounty amount of user per each reward token
	 * @dev Reward is in rTokens
	 */
	function bountyForUser(address _user) public view returns (IFeeDistribution.RewardData[] memory bounties) {
		IFeeDistribution.RewardData[] memory pending = claimableRewards(_user);
		LockedBalance[] memory locks = userLocks[_user];
		bounties = mfdHelper.getIneligibleRewards(pending, locks, lastClaimTime[_user]);
	}

	/**
	 * @notice Compound user's rewards
	 * @dev Can be auto compound or manual compound
	 * @param _user user address
	 * @param _execute whether to execute txn, or just quote (expected amount out for bounty executor and slippage)
	 */
	function claimCompound(address _user, bool _execute) public returns (uint256 tokensOut) {
		bool isAutoCompound = _user != msg.sender;

		if (isAutoCompound && !autocompoundEnabled[_user]) {
			if (_execute) {
				revert();
			} else {
				return 0;
			}
		}

		if (_execute) {
			_updateReward(_user);
		}

		uint256 pendingInRdnt = _wethToRdnt(
			_convertPendingRewardsToWeth(_user, claimableRewards(_user), false, true),
			false
		);

		// user is not eligible for AC: too low pending or too soon since last execute
		if (
			isAutoCompound &&
			(pendingInRdnt < autocompoundThreshold || block.timestamp.sub(lastAutocompound[_user]) < 1 days)
		) {
			return 0;
		}

		(uint256 wethZapped, uint256 feeInWeth) = _compoundUser(_user, isAutoCompound, _execute);

		if (isAutoCompound) {
			tokensOut = _wethToRdnt(feeInWeth, _execute);
			if (_execute) {
				rdntToken.safeApprove(address(bountyManager), tokensOut);
				lastAutocompound[_user] = block.timestamp;
			}
		} else {
			tokensOut = wethZapped;
		}
	}

	/**
	 * @notice Return expected amout our for a user to compound themself
	 * @dev has own funcs because claimBounty above doesnt handle slippage
	 */
	function quoteSelfCompound() external view returns (uint256 tokensOut) {
		tokensOut = IMFDHelper(mfdHelper).quoteSelfCompound();
	}

	/**
	 * @notice Compound msg.sender
	 * @dev has own funcs because claimBounty above doesnt handle slippage
	 * @param _quote expected amount WETH out, quoted before this txn
	 */
	function selfCompound(uint256 _quote) external returns (uint256 tokensOut) {
		require(_quote != 0);
		tokensOut = claimCompound(msg.sender, true);
		uint256 minAmountOut = _quote.sub(_quote.mul(compoundAcceptableSlippage).div(100));
		require(tokensOut >= minAmountOut);
	}

	/**
	 * @notice Compound: zap user pending rewards into locked LP
	 * @dev has own funcs because claimBounty above doesnt handle slippage
	 * @param _user address
	 * @param _takeFee whether to remove % fee from converted WETH to pay Bounty hunter
	 * @param _execute execute txn or just quote
	 */
	function _compoundUser(
		address _user,
		bool _takeFee,
		bool _execute
	) internal returns (uint256 wethZapped, uint256 feeInWeth) {
		wethZapped = _convertPendingRewardsToWeth(_user, claimableRewards(_user), _execute, true);

		if (_takeFee) {
			feeInWeth = wethZapped.mul(autocompoundFee).div(100);
			wethZapped = wethZapped.sub(feeInWeth);
		}

		if (_execute) {
			IERC20(baseToken).safeApprove(lockZap, wethZapped);
			ILockZap(lockZap).zapOnBehalf(false, wethZapped, 0, _user);
			lastClaimTime[_user] = block.timestamp;

			emit Compounded(_user, wethZapped);
		}
	}

	/**
	 * @notice Bounty amount of user per each reward token
	 * @dev returns total WETH amount from swapping all token balances in _pending array
	 * @param _user address
	 * @param _pending array of {rToken, amount}. could be their pending, or an array of penalties already
	 * removed from their pending
	 * @param _execute execute txn or just quote
	 * @param _decrementUserBalance for each rToken amount in _pending, remove from their pending balance
	 */
	function _convertPendingRewardsToWeth(
		address _user,
		IFeeDistribution.RewardData[] memory _pending,
		bool _execute,
		bool _decrementUserBalance
	) internal returns (uint256 wethOut) {
		for (uint256 i = 0; i < _pending.length; i++) {
			address token = _pending[i].token;
			uint256 removedAmount = _pending[i].amount;
			if (removedAmount == 0 || IERC20(token).balanceOf(address(this)) == 0) {
				continue;
			}

			address underlying = mfdHelper.getUnderlying(token);

			if (_execute) {
				uint256 pendingReward = rewards[_user][token].div(1e12);

				if (_decrementUserBalance) {
					require(pendingReward >= removedAmount);
					rewards[_user][token] = rewards[_user][token].sub(removedAmount.mul(1e12));
				}
				rewardData[token].balance = rewardData[token].balance.sub(removedAmount);
				emit RewardPaid(_user, token, removedAmount);

				ILendingPool lendingPool = ILendingPool(mfdHelper.getLendingPool());
				removedAmount = lendingPool.withdraw(underlying, removedAmount, address(this));
			}

			if (underlying == baseToken) {
				wethOut = wethOut.add(removedAmount);
			} else {
				if (_execute) {
					IERC20(underlying).safeApprove(uniRouter, removedAmount);
					uint256[] memory amounts = IUniswapV2Router02(uniRouter).swapExactTokensForTokens(
						removedAmount,
						0, // slippage handled after this function
						mfdHelper.getRewardToBaseRoute(underlying),
						address(this),
						block.timestamp + 10
					);
					wethOut = wethOut.add(amounts[amounts.length - 1]);
				} else {
					uint256[] memory amounts = IUniswapV2Router02(uniRouter).getAmountsOut(
						removedAmount, //amt in
						mfdHelper.getRewardToBaseRoute(underlying) //path
					);
					wethOut = wethOut.add(amounts[amounts.length - 1]);
				}
			}
		}
	}

	/**
	 * @notice shortcut for above, using users claimable rewards
	 * @param _user address
	 */
	function convertPendingRewardsToWeth(address _user) public returns (uint256 wethOut) {
		return _convertPendingRewardsToWeth(_user, claimableRewards(_user), false, false);
	}

	/**
	 * @notice given WETH, return RDNT
	 * @param _wethIn WETH in
	 * @param _execute whether to execute swap, or return expected amt out
	 */
	function _wethToRdnt(uint256 _wethIn, bool _execute) internal returns (uint256 rdntOut) {
		if (_wethIn != 0) {
			if (_execute) {
				IERC20(baseToken).safeApprove(uniRouter, _wethIn);
				uint256[] memory amounts = IUniswapV2Router02(uniRouter).swapExactTokensForTokens(
					_wethIn,
					0,
					mfdHelper.getRouteToRdnt(),
					address(this),
					block.timestamp + 600
				);
				rdntOut = amounts[amounts.length - 1];
			} else {
				uint256[] memory amounts = IUniswapV2Router02(uniRouter).getAmountsOut(
					_wethIn, //amt in
					mfdHelper.getRouteToRdnt()
				);
				rdntOut = amounts[amounts.length - 1];
			}
		}
	}
}

contracts/staking/MFDstats.sol.content:
// SPDX-License-Identifier: MIT

pragma solidity 0.7.6;
pragma abicoder v2;

import "@uniswap/v2-core/contracts/interfaces/IUniswapV2Pair.sol";
import "../dependencies/openzeppelin/contracts/IERC20Detailed.sol";
import "../dependencies/openzeppelin/contracts/IERC20.sol";
import "../dependencies/openzeppelin/upgradeability/Initializable.sol";
import "../dependencies/openzeppelin/upgradeability/OwnableUpgradeable.sol";
import "../misc/interfaces/IAaveOracle.sol";
import "../dependencies/openzeppelin/contracts/SafeMath.sol";
import "../interfaces/IChainlinkAggregator.sol";
import {IMultiFeeDistribution} from '../interfaces/IMultiFeeDistribution.sol';
import {IMiddleFeeDistribution} from '../interfaces/IMiddleFeeDistribution.sol';

contract MFDstats is Initializable, OwnableUpgradeable {
    using SafeMath for uint256;

    address private _aaveOracle;
    address private _emissionsReserve;
    address private _mfd;

    struct MFDTransfer {
        uint256 timestamp;
        uint256 usdValue;
        uint256 lpUsdValue;
    }

    struct AssetAddresses {
        uint256 count;
        mapping(uint256 => address) assetAddress;
        mapping(uint256 => string) assetSymbol;
        mapping(address => uint256) indexOfAddress;
    }

    struct TrackPerAsset {
        address assetAddress;
        string assetSymbol;
        uint256 usdValue;
        uint256 lpUsdValue;
    }

    struct AddTransferParam {
        address asset;
        uint256 amount;
        address treasury;
    }

    AssetAddresses private allAddresses;

    mapping(address => uint256) private _totalPerAsset;
    mapping(address => uint256) private _lpTotalPerAsset;
    mapping(address => MFDTransfer[]) private mfdTransfersPerAsset;

    IMiddleFeeDistribution public middleFee;

    uint256 public constant DAY_SECONDS = 86400;
    uint8 public constant DECIMALS = 18;
    uint256 public constant RATIO_DIVISOR = 10000;

    address[] public vests; 

    event NewTransferAdded(address indexed asset, uint256 usdValue, uint256 lpUsdValue);

    function initialize(
        address aaveOracle, address emissionsReserve
    ) public initializer {
        _aaveOracle = aaveOracle;
        _emissionsReserve = emissionsReserve;
        __Ownable_init();
    }

    function getPriceDecimal (address assetAddress) external view returns (uint8) {
        address sourceOfAsset = IAaveOracle(_aaveOracle).getSourceOfAsset(
            assetAddress
        );
        uint8 priceDecimal = IChainlinkAggregator(sourceOfAsset).decimals();
        return priceDecimal;
    }

    function setMiddleFee(IMiddleFeeDistribution _middleFee) external onlyOwner {
        middleFee = _middleFee;
    }

    function addTransfer(AddTransferParam memory param) external {
        require(middleFee.isRewardToken(msg.sender), "!rToken");

        uint256 lpLockingRewardRatio = IMiddleFeeDistribution(param.treasury).lpLockingRewardRatio();
        uint256 operationExpenseRatio = IMiddleFeeDistribution(param.treasury).operationExpenseRatio();
        address operationExpenses = IMiddleFeeDistribution(param.treasury).operationExpenses();
        uint256 assetPrice = IAaveOracle(_aaveOracle).getAssetPrice(
            param.asset
        );
        address sourceOfAsset = IAaveOracle(_aaveOracle).getSourceOfAsset(
            param.asset
        );

        if (operationExpenses != address(0) && operationExpenseRatio > 0) {
            uint256 opExAmount = param.amount.mul(operationExpenseRatio).div(RATIO_DIVISOR);
            param.amount = param.amount.sub(opExAmount);
        }
        uint8 priceDecimal = IChainlinkAggregator(sourceOfAsset).decimals();
        uint8 assetDecimals = IERC20Detailed(param.asset).decimals();
        uint256 usdValue = assetPrice
            .mul(param.amount)
            .mul(10**DECIMALS)
            .div(10**priceDecimal)
            .div(10**assetDecimals);
        uint256 lpUsdValue = usdValue.mul(lpLockingRewardRatio).div(RATIO_DIVISOR);
        usdValue = usdValue.sub(lpUsdValue);

        uint256 index;

        if (allAddresses.indexOfAddress[param.asset] == 0) {
            allAddresses.count++;
            allAddresses.assetAddress[allAddresses.count] = param.asset;
            allAddresses.assetSymbol[allAddresses.count] = IERC20Detailed(
                param.asset
            ).symbol();
            allAddresses.indexOfAddress[param.asset] = allAddresses.count;
        }
        _totalPerAsset[param.asset] = _totalPerAsset[param.asset].add(usdValue);
        _lpTotalPerAsset[param.asset] = _lpTotalPerAsset[param.asset].add(
            lpUsdValue
        );

        for (uint256 i = 0; i < mfdTransfersPerAsset[param.asset].length; i++) {
            if (
                block.timestamp.sub(
                    mfdTransfersPerAsset[param.asset][i].timestamp
                ) <= DAY_SECONDS
            ) {
                index = i;
                break;
            }
        }

        for (
            uint256 i = index;
            i < mfdTransfersPerAsset[param.asset].length;
            i++
        ) {
            mfdTransfersPerAsset[param.asset][i - index] = mfdTransfersPerAsset[
                param.asset
            ][i];
        }

        for (uint256 i = 0; i < index; i++) {
            mfdTransfersPerAsset[param.asset].pop();
        }

        mfdTransfersPerAsset[param.asset].push(
            MFDTransfer(block.timestamp, usdValue, lpUsdValue)
        );

        emit NewTransferAdded(param.asset, usdValue, lpUsdValue);
    }

    function getTotal() external view returns (TrackPerAsset[] memory) {
        TrackPerAsset[] memory totalPerAsset = new TrackPerAsset[](
            allAddresses.count + 1
        );
        uint256 total;
        uint256 lpTotal;
        for (uint256 i = 1; i <= allAddresses.count; i++) {
            total = total.add(_totalPerAsset[allAddresses.assetAddress[i]]);
            lpTotal = lpTotal.add(
                _lpTotalPerAsset[allAddresses.assetAddress[i]]
            );

            totalPerAsset[i] = TrackPerAsset(
                allAddresses.assetAddress[i],
                allAddresses.assetSymbol[i],
                _totalPerAsset[allAddresses.assetAddress[i]],
                _lpTotalPerAsset[allAddresses.assetAddress[i]]
            );
        }
        totalPerAsset[0] = TrackPerAsset(address(0), "", total, lpTotal);
        return totalPerAsset;
    }

    function getLastDayTotal() external view returns (TrackPerAsset[] memory) {
        TrackPerAsset[] memory lastDayTotalPerAsset = new TrackPerAsset[](
            allAddresses.count + 1
        );
        uint256 lastdayTotal;
        uint256 lpLastDayTotal;

        for (uint256 i = 1; i <= allAddresses.count; i++) {
            uint256 assetLastDayTotal;
            uint256 lpAssetLastDayTotal;

            assert(mfdTransfersPerAsset[allAddresses.assetAddress[i]].length > 0);
            for (
                uint256 j = mfdTransfersPerAsset[
                    allAddresses.assetAddress[i]
                ].length.sub(1);
                ;
                j--
            ) {
                if (
                    block.timestamp.sub(
                        mfdTransfersPerAsset[allAddresses.assetAddress[i]][
                            j
                        ].timestamp
                    ) <= DAY_SECONDS
                ) {
                    assetLastDayTotal = assetLastDayTotal.add(
                        mfdTransfersPerAsset[allAddresses.assetAddress[i]][
                            j
                        ].usdValue
                    );
                    lpAssetLastDayTotal = lpAssetLastDayTotal.add(
                        mfdTransfersPerAsset[allAddresses.assetAddress[i]][
                            j
                        ].lpUsdValue
                    );
                } else {
                    break;
                }
                if (j == 0) break;
            }

            lastdayTotal = lastdayTotal.add(assetLastDayTotal);
            lpLastDayTotal = lpLastDayTotal.add(lpAssetLastDayTotal);
            lastDayTotalPerAsset[i] = TrackPerAsset(
                allAddresses.assetAddress[i],
                allAddresses.assetSymbol[i],
                assetLastDayTotal,
                lpAssetLastDayTotal
            );
        }

        lastDayTotalPerAsset[0] = TrackPerAsset(
            address(0),
            "",
            lastdayTotal,
            lpLastDayTotal
        );

        return lastDayTotalPerAsset;
    }

    function calcBalanceSum (
        address daoTreasuryAddress,
        address chef,
        address migration,
        address bountyManager,
        address mfd,
        address emissionsReserve
    ) internal view returns (uint256) {
        IERC20 rdnt = IERC20(middleFee.getRdntTokenAddress());
        uint256 daoBalance = rdnt.balanceOf(daoTreasuryAddress);
        uint256 chefBalance = rdnt.balanceOf(chef);
        uint256 migrationBalance = rdnt.balanceOf(migration);
        uint256 bountyBalance = rdnt.balanceOf(bountyManager);
        // mfd balance
        uint256 mfdLockedBalance = rdnt.balanceOf(mfd);
        uint256 emissionsReserveBalance = rdnt.balanceOf(emissionsReserve);

        return daoBalance
            + chefBalance
            + migrationBalance
            + bountyBalance
            + mfdLockedBalance
            + emissionsReserveBalance;
    }

    function calcVestsBalances() internal view returns(uint256) {
        uint256 total;
        IERC20 rdnt = IERC20(middleFee.getRdntTokenAddress());
        for(uint256 i = 0; i < vests.length; i++) {
            total = total.add(rdnt.balanceOf(vests[i]));
        }
        return total;
    }

    function getCirculatingSupply(
        address _chef,
        address _bountyManager,
        address _migration
    ) external view returns (uint256) {
        IMultiFeeDistribution mfd = IMultiFeeDistribution(middleFee.getMultiFeeDistributionAddress());
        IMultiFeeDistribution lpMfd = IMultiFeeDistribution(middleFee.getLPFeeDistributionAddress());
        IERC20 rdnt = IERC20(middleFee.getRdntTokenAddress());

        address daoTreasuryAddress = mfd.daoTreasury();
        uint256 balanceSum = calcBalanceSum(
            daoTreasuryAddress,
            _chef,
            _migration,
            _bountyManager,
            address(mfd),
            _emissionsReserve
        );
        uint256 vestsBal = calcVestsBalances();
        // lp fee distribution balance
        IERC20 lpToken = IERC20(lpMfd.stakingToken());
        uint256 lockedLPAmount = lpToken.balanceOf(address(lpMfd));
        uint256 lpTotalSupply = lpToken.totalSupply();
        uint256 lpfdLockedBalance;

        (uint256 reserve0, uint256 reserve1, ) = IUniswapV2Pair(address(lpToken)).getReserves();
        if (IUniswapV2Pair(address(lpToken)).token0() == address(rdnt)) {
            lpfdLockedBalance = reserve0 * lockedLPAmount / lpTotalSupply;
        } else {
            lpfdLockedBalance = reserve1 * lockedLPAmount / lpTotalSupply;
        }
        //total supply
        uint256 totalSupply = rdnt.totalSupply();
        uint256 totalBalance = lpfdLockedBalance.add(balanceSum).add(vestsBal);
        if(totalSupply >= totalBalance) return totalSupply.sub(totalBalance);
        else return 0;
    }

    function addVest (address _vest) external onlyOwner {
        vests.push(_vest);
    }
}

contracts/staking/MiddleFeeDistribution..content:
// SPDX-License-Identifier: MIT
pragma solidity 0.7.6;
pragma abicoder v2;

import "../interfaces/IMiddleFeeDistribution.sol";
import "../interfaces/IMultiFeeDistribution.sol";
import "../interfaces/IMintableToken.sol";

import "../dependencies/openzeppelin/contracts/IERC20.sol";
import "../dependencies/openzeppelin/contracts/IERC20Detailed.sol";
import "../dependencies/openzeppelin/contracts/SafeERC20.sol";
import "../dependencies/openzeppelin/contracts/SafeMath.sol";
import "../dependencies/openzeppelin/upgradeability/Initializable.sol";
import "../dependencies/openzeppelin/upgradeability/OwnableUpgradeable.sol";

/// @title Fee distributor inside 
/// @author Radiant
/// @dev All function calls are currently implemented without side effects
contract MiddleFeeDistribution is IMiddleFeeDistribution, Initializable, OwnableUpgradeable {
    using SafeMath for uint256;
    using SafeERC20 for IERC20;

    /// @notice RDNT token
    IMintableToken public rdntToken;

    /// @notice Fee distributor contract for lp locking
    IMultiFeeDistribution public lpFeeDistribution;

    /// @notice Fee distributor contract for earnings and RDNT lockings
    IMultiFeeDistribution public multiFeeDistribution;

    /// @notice Reward ratio for lp locking in bips
    uint256 public override lpLockingRewardRatio;

    /// @notice Reward ratio for operation expenses
    uint256 public override operationExpenseRatio;

    uint256 public constant RATIO_DIVISOR = 10000;

    mapping(address => bool) public override isRewardToken;

    /// @notice Operation Expense account
    address public override operationExpenses;

    /// @notice Admin address
    address public admin;

    // MFDStats address
    address internal _mfdStats;

    /********************** Events ***********************/

    /// @notice Emitted when ERC20 token is recovered
    event Recovered(address token, uint256 amount);

    /// @notice Emitted when reward token is forwarded
    event ForwardReward(address token, uint256 amount);

    /// @notice Emitted when OpEx info is updated
    event SetOperationExpenses(address opEx, uint256 ratio);

    /// @notice Emitted when LP locking reward ratio is set 
    event LpLockingRewardRatioUpdated(uint256 _lpLockingRewardRatio);

    /// @notice Emitted when lp fee distribution is set
    event LPFeeDistributionUpdated(IMultiFeeDistribution _lpFeeDistribution);

    /// @notice Emitted when operation expenses is set
    event OperationExpensesUpdated(address _operationExpenses, uint256 _operationExpenseRatio);

    /**
    * @dev Throws if called by any account other than the admin or owner.
    */
    modifier onlyAdminOrOwner() {
        require(admin == _msgSender() || owner() == _msgSender(), 'caller is not the admin or owner');
        _;
    }

    function initialize(
        address _rdntToken,
        address mfdStats,
        IMultiFeeDistribution _lpFeeDistribution,
        IMultiFeeDistribution _multiFeeDistribution
    ) public initializer {
        __Ownable_init();

        rdntToken = IMintableToken(_rdntToken);
        _mfdStats = mfdStats;
        lpFeeDistribution = _lpFeeDistribution;
        multiFeeDistribution = _multiFeeDistribution;

        lpLockingRewardRatio = 10000;
        admin = msg.sender;
    }

    function getMFDstatsAddress () external view override returns (address) {
        return _mfdStats;
    }

    function getRdntTokenAddress () external view override returns (address) {
        return address(rdntToken);
    }

    function getLPFeeDistributionAddress () external view override returns (address) {
        return address(lpFeeDistribution);
    }

    function getMultiFeeDistributionAddress () external view override returns (address) {
        return address(multiFeeDistribution);
    }

    /**
     * @notice Returns lock information of a user.
     * @dev It currently returns just MFD infos.
     */
    function lockedBalances(
        address user
    ) view external override returns (
        uint256 total,
        uint256 unlockable,
        uint256 locked,
        uint256 lockedWithMultiplier,
        LockedBalance[] memory lockData
    ) {
        return multiFeeDistribution.lockedBalances(user);
    }

    /**
     * @notice Set reward ratio for lp token locking
     */
    function setLpLockingRewardRatio(uint256 _lpLockingRewardRatio) external onlyAdminOrOwner {
        require(_lpLockingRewardRatio <= RATIO_DIVISOR, "Invalid ratio");
        lpLockingRewardRatio = _lpLockingRewardRatio;
        emit LpLockingRewardRatioUpdated(_lpLockingRewardRatio);
    }

    /**
     * @notice Set lp fee distribution contract
     */
    function setLPFeeDistribution(IMultiFeeDistribution _lpFeeDistribution) external onlyAdminOrOwner {
        lpFeeDistribution = _lpFeeDistribution;
        emit LPFeeDistributionUpdated(_lpFeeDistribution);
    }

    /**
     * @notice Set operation expenses account
     */
    function setOperationExpenses(address _operationExpenses, uint256 _operationExpenseRatio) external onlyAdminOrOwner {
        require(_operationExpenseRatio <= RATIO_DIVISOR, "Invalid ratio");
        operationExpenses = _operationExpenses;
        operationExpenseRatio = _operationExpenseRatio;
        emit OperationExpensesUpdated(_operationExpenses, _operationExpenseRatio);
    }

    /**
     * @notice Add a new reward token to be distributed to stakers
     */
    function addReward(address _rewardsToken) external override onlyAdminOrOwner {
        multiFeeDistribution.addReward(_rewardsToken);
        lpFeeDistribution.addReward(_rewardsToken);
        isRewardToken[_rewardsToken] = true;
    }

    /**
     * @notice Added to support recovering LP Rewards from other systems such as BAL to be distributed to holders
     */
    function forwardReward(address[] memory _rewardTokens) external override {
        require(
            msg.sender == address(lpFeeDistribution) ||
            msg.sender == address(multiFeeDistribution)
        );

        for (uint256 i = 0; i < _rewardTokens.length; i += 1) {
            uint256 total = IERC20(_rewardTokens[i]).balanceOf(address(this));

            if (operationExpenses != address(0) && operationExpenseRatio != 0) {
                uint256 opExAmount = total.mul(operationExpenseRatio).div(RATIO_DIVISOR);
                if (opExAmount != 0) {
                    IERC20(_rewardTokens[i]).safeTransfer(operationExpenses, opExAmount);
                }
                total = total.sub(opExAmount);
            }
            total = IERC20(_rewardTokens[i]).balanceOf(address(this));
            uint256 lpReward = total.mul(lpLockingRewardRatio).div(RATIO_DIVISOR);
            if (lpReward != 0) {
                IERC20(_rewardTokens[i]).safeTransfer(address(lpFeeDistribution), lpReward);
            }
            uint256 rdntReward = IERC20(_rewardTokens[i]).balanceOf(address(this));
            if (rdntReward != 0) {
                IERC20(_rewardTokens[i]).safeTransfer(address(multiFeeDistribution), rdntReward);
            }
        }
    }

    /**
     * @notice Added to support recovering LP Rewards from other systems such as BAL to be distributed to holders
     */
    function recoverERC20(address tokenAddress, uint256 tokenAmount) external onlyOwner {
        IERC20(tokenAddress).safeTransfer(owner(), tokenAmount);
        emit Recovered(tokenAddress, tokenAmount);
    }
}

contracts/staking/MultiFeeDistribution.s.content:
// SPDX-License-Identifier: MIT
pragma solidity 0.7.6;
pragma abicoder v2;

import "../interfaces/IChefIncentivesController.sol";
import "../interfaces/IMiddleFeeDistribution.sol";
import "../interfaces/IBounty.sol";
import {IMultiFeeDistribution} from "../interfaces/IMultiFeeDistribution.sol";
import "../interfaces/IMintableToken.sol";
import "../interfaces/ILockerList.sol";
import "../interfaces/LockedBalance.sol";
import "../interfaces/IChainlinkAggregator.sol";
import "../interfaces/IPriceProvider.sol";
import "../dependencies/openzeppelin/contracts/IERC20.sol";
import "../dependencies/openzeppelin/contracts/IERC20Detailed.sol";
import "../dependencies/openzeppelin/contracts/SafeERC20.sol";
import "../dependencies/openzeppelin/contracts/SafeMath.sol";
import "../dependencies/openzeppelin/upgradeability/Initializable.sol";
import "../dependencies/openzeppelin/upgradeability/OwnableUpgradeable.sol";

/// @title Multi Fee Distribution Contract
/// @author Radiant
/// @dev All function calls are currently implemented without side effects
contract MultiFeeDistribution is IMultiFeeDistribution, Initializable, OwnableUpgradeable {
	using SafeMath for uint256;
	using SafeERC20 for IERC20;
	using SafeERC20 for IMintableToken;

	struct Reward {
		uint256 periodFinish;
		uint256 rewardPerSecond;
		uint256 lastUpdateTime;
		uint256 rewardPerTokenStored;
		// tracks already-added balances to handle accrued interest in aToken rewards
		// for the stakingToken this value is unused and will always be 0
		uint256 balance;
	}

	struct Balances {
		uint256 total; // sum of earnings and lockings; no use when LP and RDNT is different
		uint256 unlocked; // RDNT token
		uint256 locked; // LP token or RDNT token
		uint256 lockedWithMultiplier; // Multiplied locked amount
		uint256 earned; // RDNT token
	}

	address private _priceProvider;

	/********************** Constants ***********************/

	uint256 public constant QUART = 25000; //  25%
	uint256 public constant HALF = 65000; //  65%
	uint256 public constant WHOLE = 100000; // 100%

	/// @notice Proportion of burn amount
	uint256 public BURN;

	/// @notice Duration that rewards are streamed over
	uint256 public REWARDS_DURATION;

	/// @notice Duration that rewards loop back
	uint256 public REWARDS_LOOKBACK;

	/// @notice Multiplier for earnings, fixed to 1
	// uint256 public constant DEFAULT_MUTLIPLIER = 1;

	/// @notice Default lock index
	uint256 public constant DEFAULT_LOCK_INDEX = 1;

	/// @notice Duration of lock/earned penalty period, used for earnings
	uint256 public DEFAULT_LOCK_DURATION;

	/// @notice Duration of vesting RDNT
	uint256 public VEST_DURATION;

	/********************** Contract Addresses ***********************/

	/// @notice Address of Middle Fee Distribution Contract
	IMiddleFeeDistribution public middleFeeDistribution;

	/// @notice Address of CIC contract
	IChefIncentivesController public incentivesController;

	/// @notice Address of RDNT
	IMintableToken public rdntToken;

	/// @notice Address of LP token
	address public override stakingToken;

	// Address of Lock Zapper
	address internal lockZap;

	/********************** Lock & Earn Info ***********************/

	// Private mappings for balance data
	mapping(address => Balances) private balances;
	mapping(address => LockedBalance[]) internal userLocks;
	mapping(address => LockedBalance[]) private userEarnings;

	/// @notice Total locked value
	uint256 public lockedSupply;

	/// @notice Total locked value in multipliers
	uint256 public lockedSupplyWithMultiplier;

	// Time lengths
	uint256[] internal lockPeriod;

	// Multipliers
	uint256[] internal rewardMultipliers;

	/********************** Reward Info ***********************/

	/// @notice Reward tokens being distributed
	address[] public rewardTokens;

	/// @notice Reward data per token
	mapping(address => Reward) public rewardData;

	/// @notice user -> reward token -> rpt; RPT for paid amount
	mapping(address => mapping(address => uint256)) public userRewardPerTokenPaid;

	/// @notice user -> reward token -> amount; used to store reward amount
	mapping(address => mapping(address => uint256)) public rewards;

	/********************** Other Info ***********************/

	/// @notice DAO wallet
	address public override daoTreasury;

	/// @notice treasury wallet
	address public STARFLEET_TREASURY;

	/// @notice Addresses approved to call mint
	mapping(address => bool) public minters;

	// Addresses to relock
	mapping(address => bool) public override autoRelockDisabled;

	// Default lock index for relock
	mapping(address => uint256) public override defaultLockIndex;

	/// @notice Flag to prevent more minter addings
	bool public mintersAreSet;

	// Users list
	ILockerList public userlist;

	mapping(address => uint256) public lastClaimTime;

	address public BountyManager;

	// to prevent unbounded lock length iteration during withdraw/clean

	/********************** Events ***********************/

	event RewardAdded(uint256 reward);
	event Staked(address indexed user, uint256 amount, bool locked);
	event Locked(address indexed user, uint256 usdValue, bool isLP);
	event Withdrawn(address indexed user, address indexed token, uint256 receivedAmount, uint256 penalty, uint256 burn);
	event RewardPaid(address indexed user, address indexed rewardToken, uint256 reward);
	event IneligibleRewardRemoved(address indexed user, address indexed rewardToken, uint256 reward);
	event RewardsDurationUpdated(address token, uint256 newDuration);
	event Recovered(address token, uint256 amount);
	event Relocked(address indexed user, uint256 amount, uint256 lockIndex);

	/**
	 * @dev Constructor
	 *  First reward MUST be the RDNT token or things will break
	 *  related to the 50% penalty and distribution to locked balances.
	 * @param _rdntToken RDNT token address.
	 * @param _rewardsDuration set reward stream time.
	 * @param _rewardsLookback reward lookback
	 * @param _lockDuration lock duration
	 */
	function initialize(
		address _rdntToken,
		address _lockZap,
		address _dao,
		address _userlist,
		address priceProvider,
		uint256 _rewardsDuration,
		uint256 _rewardsLookback,
		uint256 _lockDuration,
		uint256 _burnRatio,
		uint256 _vestDuration
	) public initializer {
		// require(_rdntToken != address(0));
		// require(_lockZap != address(0));
		// require(_dao != address(0));
		// require(_userlist != address(0));
		// require(priceProvider != address(0));
		// require(_rewardsDuration != uint256(0));
		// require(_rewardsLookback != uint256(0));
		// require(_lockDuration != uint256(0));
		// require(_vestDuration != uint256(0));
		// require(_burnRatio <= WHOLE);
		// require(_rewardsLookback <= _rewardsDuration);

		__Ownable_init();

		rdntToken = IMintableToken(_rdntToken);
		lockZap = _lockZap;
		daoTreasury = _dao;
		_priceProvider = priceProvider;
		userlist = ILockerList(_userlist);
		rewardTokens.push(_rdntToken);
		rewardData[_rdntToken].lastUpdateTime = block.timestamp;

		REWARDS_DURATION = _rewardsDuration;
		REWARDS_LOOKBACK = _rewardsLookback;
		DEFAULT_LOCK_DURATION = _lockDuration;
		BURN = _burnRatio;
		VEST_DURATION = _vestDuration;
	}

	/********************** Setters ***********************/

	/**
	 * @notice Set minters
	 * @dev Can be called only once
	 */
	function setMinters(address[] memory _minters) external onlyOwner {
		require(!mintersAreSet);
		for (uint256 i; i < _minters.length; i++) {
			minters[_minters[i]] = true;
		}
		mintersAreSet = true;
	}

	function addBountyContract(address _bounty) external onlyOwner {
		BountyManager = _bounty;
		minters[_bounty] = true;
	}

	/**
	 * @notice Add a new reward token to be distributed to stakers.
	 */
	function setLockTypeInfo(uint256[] memory _lockPeriod, uint256[] memory _rewardMultipliers) external onlyOwner {
		require(_lockPeriod.length == _rewardMultipliers.length);
		delete lockPeriod;
		delete rewardMultipliers;
		for (uint256 i = 0; i < _lockPeriod.length; i += 1) {
			lockPeriod.push(_lockPeriod[i]);
			rewardMultipliers.push(_rewardMultipliers[i]);
		}
	}

	/**
	 * @notice Set CIC, MFD and Treasury.
	 */
	function setAddresses(
		IChefIncentivesController _controller,
		IMiddleFeeDistribution _middleFeeDistribution,
		address _treasury
	) external onlyOwner {
		incentivesController = _controller;
		middleFeeDistribution = _middleFeeDistribution;
		STARFLEET_TREASURY = _treasury;
	}

	/**
	 * @notice Set LP token.
	 */
	function setLPToken(address _stakingToken) external onlyOwner {
		require(stakingToken == address(0));
		stakingToken = _stakingToken;
	}

	/**
	 * @notice Add a new reward token to be distributed to stakers.
	 */
	function addReward(address _rewardToken) external override {
		require(minters[msg.sender]);
		require(rewardData[_rewardToken].lastUpdateTime == 0);
		rewardTokens.push(_rewardToken);
		rewardData[_rewardToken].lastUpdateTime = block.timestamp;
		rewardData[_rewardToken].periodFinish = block.timestamp;
	}

	/********************** View functions ***********************/

	/**
	 * @notice Set default lock type index for user relock.
	 */
	function setDefaultRelockTypeIndex(uint256 _index) external override {
		require(_index < lockPeriod.length);
		defaultLockIndex[msg.sender] = _index;
	}

	function getLockDurations() external view returns (uint256[] memory) {
		return lockPeriod;
	}

	function getLockMultipliers() external view returns (uint256[] memory) {
		return rewardMultipliers;
	}

	/**
	 * @notice Set relock status
	 */
	function setRelock(bool _status) external {
		if (BountyManager != address(0)) {
			IBounty(BountyManager).onRelockUpdate(msg.sender, !autoRelockDisabled[msg.sender], _status);
		}
		autoRelockDisabled[msg.sender] = !_status;
	}

	/**
	 * @notice Returns all locks of a user.
	 */
	function lockInfo(address user) external view override returns (LockedBalance[] memory) {
		return userLocks[user];
	}

	/**
	 * @notice Added to support recovering LP Rewards from other systems such as BAL to be distributed to holders.
	 */
	// function recoverERC20(address tokenAddress, uint256 tokenAmount) external onlyOwner {
	// 	require(rewardData[tokenAddress].lastUpdateTime == 0);
	// 	IERC20(tokenAddress).safeTransfer(owner(), tokenAmount);
	// 	emit Recovered(tokenAddress, tokenAmount);
	// }

	/**
	 * @notice Withdraw and restake assets.
	 */
	function relock() external {
		uint256 amount = _withdrawExpiredLocksFor(msg.sender, true, true, userLocks[msg.sender].length);
		_stake(amount, msg.sender, defaultLockIndex[msg.sender], false);
		emit Relocked(msg.sender, amount, defaultLockIndex[msg.sender]);
	}

	/**
	 * @notice Total balance of an account, including unlocked, locked and earned tokens.
	 */
	function totalBalance(address user) external view override returns (uint256 amount) {
		if (stakingToken == address(rdntToken)) {
			return balances[user].total;
		}
		return balances[user].locked;
	}

	/**
	 * @notice Information on a user's lockings
	 * @return total balance of locks
	 * @return unlockable balance
	 * @return locked balance
	 * @return lockedWithMultiplier
	 * @return lockData which is an array of locks
	 */
	function lockedBalances(address user)
		public
		view
		override
		returns (
			uint256 total,
			uint256 unlockable,
			uint256 locked,
			uint256 lockedWithMultiplier,
			LockedBalance[] memory lockData
		)
	{
		LockedBalance[] storage locks = userLocks[user];
		uint256 idx;
		for (uint256 i = 0; i < locks.length; i++) {
			if (locks[i].unlockTime > block.timestamp) {
				if (idx == 0) {
					lockData = new LockedBalance[](locks.length - i);
				}
				lockData[idx] = locks[i];
				idx++;
				locked = locked.add(locks[i].amount);
				lockedWithMultiplier = lockedWithMultiplier.add(locks[i].amount.mul(locks[i].multiplier));
			} else {
				unlockable = unlockable.add(locks[i].amount);
			}
		}
		return (balances[user].locked, unlockable, locked, lockedWithMultiplier, lockData);
	}

	/**
	 * @notice Earnings which is locked yet
	 * @dev Earned balances may be withdrawn immediately for a 50% penalty.
	 * @return total earnings
	 * @return unlocked earnings
	 * @return earningsData which is an array of all infos
	 */
	function earnedBalances(address user)
		public
		view
		returns (
			uint256 total,
			uint256 unlocked,
			EarnedBalance[] memory earningsData
		)
	{
		unlocked = balances[user].unlocked;
		LockedBalance[] storage earnings = userEarnings[user];
		uint256 idx;
		for (uint256 i = 0; i < earnings.length; i++) {
			if (earnings[i].unlockTime > block.timestamp) {
				if (idx == 0) {
					earningsData = new EarnedBalance[](earnings.length - i);
				}
				(, uint256 penaltyAmount, , ) = ieeWithdrawableBalances(user, earnings[i].unlockTime);
				earningsData[idx].amount = earnings[i].amount;
				earningsData[idx].unlockTime = earnings[i].unlockTime;
				earningsData[idx].penalty = penaltyAmount;
				idx++;
				total = total.add(earnings[i].amount);
			} else {
				unlocked = unlocked.add(earnings[i].amount);
			}
		}
		return (total, unlocked, earningsData);
	}

	/**
	 * @notice Final balance received and penalty balance paid by user upon calling exit.
	 * @dev This is earnings, not locks.
	 */
	function withdrawableBalance(address user)
		public
		view
		returns (
			uint256 amount,
			uint256 penaltyAmount,
			uint256 burnAmount
		)
	{
		uint256 earned = balances[user].earned;
		if (earned > 0) {
			uint256 length = userEarnings[user].length;
			for (uint256 i = 0; i < length; i++) {
				uint256 earnedAmount = userEarnings[user][i].amount;
				if (earnedAmount == 0) continue;
				(, , uint256 newPenaltyAmount, uint256 newBurnAmount) = _penaltyInfo(userEarnings[user][i]);
				penaltyAmount = penaltyAmount.add(newPenaltyAmount);
				burnAmount = burnAmount.add(newBurnAmount);
			}
		}
		amount = balances[user].unlocked.add(earned).sub(penaltyAmount);
		return (amount, penaltyAmount, burnAmount);
	}

	function _penaltyInfo(LockedBalance memory earning)
		internal
		view
		returns (
			uint256 amount,
			uint256 penaltyFactor,
			uint256 penaltyAmount,
			uint256 burnAmount
		)
	{
		if (earning.unlockTime > block.timestamp) {
			// 90% on day 1, decays to 25% on day 90
			penaltyFactor = earning.unlockTime.sub(block.timestamp).mul(HALF).div(VEST_DURATION).add(QUART); // 25% + timeLeft/VEST_DURATION * 65%
		}
		penaltyAmount = earning.amount.mul(penaltyFactor).div(WHOLE);
		burnAmount = penaltyAmount.mul(BURN).div(WHOLE);
		amount = earning.amount.sub(penaltyAmount);
	}

	/********************** Reward functions ***********************/

	/**
	 * @notice Reward amount of the duration.
	 * @param _rewardToken for the reward
	 */
	function getRewardForDuration(address _rewardToken) external view returns (uint256) {
		return rewardData[_rewardToken].rewardPerSecond.mul(REWARDS_DURATION).div(1e12);
	}

	/**
	 * @notice Returns reward applicable timestamp.
	 */
	function lastTimeRewardApplicable(address _rewardToken) public view returns (uint256) {
		uint256 periodFinish = rewardData[_rewardToken].periodFinish;
		return block.timestamp < periodFinish ? block.timestamp : periodFinish;
	}

	/**
	 * @notice Reward amount per token
	 * @dev Reward is distributed only for locks.
	 * @param _rewardToken for reward
	 */
	function rewardPerToken(address _rewardToken) public view returns (uint256 rptStored) {
		rptStored = rewardData[_rewardToken].rewardPerTokenStored;
		if (lockedSupplyWithMultiplier > 0) {
			uint256 newReward = lastTimeRewardApplicable(_rewardToken).sub(rewardData[_rewardToken].lastUpdateTime).mul(
				rewardData[_rewardToken].rewardPerSecond
			);
			rptStored = rptStored.add(newReward.mul(1e18).div(lockedSupplyWithMultiplier));
		}
	}

	/**
	 * @notice Address and claimable amount of all reward tokens for the given account.
	 * @param account for rewards
	 */
	function claimableRewards(address account)
		public
		view
		override
		returns (IFeeDistribution.RewardData[] memory rewardsData)
	{
		rewardsData = new IFeeDistribution.RewardData[](rewardTokens.length);
		for (uint256 i = 0; i < rewardsData.length; i++) {
			rewardsData[i].token = rewardTokens[i];
			rewardsData[i].amount = _earned(
				account,
				rewardsData[i].token,
				balances[account].lockedWithMultiplier,
				rewardPerToken(rewardsData[i].token)
			).div(1e12);
		}
		return rewardsData;
	}

	/********************** Operate functions ***********************/

	/**
	 * @notice Stake tokens to receive rewards.
	 * @dev Locked tokens cannot be withdrawn for DEFAULT_LOCK_DURATION and are eligible to receive rewards.
	 */
	function stake(
		uint256 amount,
		address onBehalfOf,
		uint256 typeIndex
	) external override {
		// Exceptional case is
		// - User locked, default index is 1
		// - User set default index as 0
		// - User unlocked all
		// - User locks again, default index is reset to 1
		if (defaultLockIndex[onBehalfOf] == 0 && userLocks[onBehalfOf].length == 0) {
			defaultLockIndex[onBehalfOf] = DEFAULT_LOCK_INDEX;
		}
		_stake(amount, onBehalfOf, typeIndex, false);
	}

	function _stake(
		uint256 amount,
		address onBehalfOf,
		uint256 typeIndex,
		bool isRelock
	) internal {
		if (amount == 0) return;
		if (BountyManager != address(0)) {
			require(amount >= IBounty(BountyManager).minDLPBalance());
		}
		require(typeIndex < lockPeriod.length);

		incentivesController.beforeLockUpdate(onBehalfOf);

		_updateReward(onBehalfOf);

		uint256 transferAmount = amount;
		if (onBehalfOf == msg.sender || msg.sender == lockZap) {
			uint256 withdrawnAmt;
			if (!autoRelockDisabled[onBehalfOf]) {
				withdrawnAmt = _withdrawExpiredLocksFor(onBehalfOf, true, false, userLocks[onBehalfOf].length);
				amount = amount.add(withdrawnAmt);
			} else {
				_withdrawExpiredLocksFor(onBehalfOf, true, true, userLocks[onBehalfOf].length);
			}
		}

		Balances storage bal = balances[onBehalfOf];
		bal.total = bal.total.add(amount);

		bal.locked = bal.locked.add(amount);
		lockedSupply = lockedSupply.add(amount);

		bal.lockedWithMultiplier = bal.lockedWithMultiplier.add(amount.mul(rewardMultipliers[typeIndex]));
		lockedSupplyWithMultiplier = lockedSupplyWithMultiplier.add(amount.mul(rewardMultipliers[typeIndex]));

		_insertLock(
			onBehalfOf,
			LockedBalance({
				amount: amount,
				unlockTime: block.timestamp.add(lockPeriod[typeIndex]),
				multiplier: rewardMultipliers[typeIndex],
				duration: lockPeriod[typeIndex]
			})
		);

		userlist.addToList(onBehalfOf);

		if (!isRelock) {
			IERC20(stakingToken).safeTransferFrom(msg.sender, address(this), transferAmount);
		}

		incentivesController.afterLockUpdate(onBehalfOf);

		uint256 stakingTokenPrice;
		if (stakingToken == address(rdntToken)) {
			stakingTokenPrice = IPriceProvider(_priceProvider).getTokenPriceUsd();
		} else {
			stakingTokenPrice = IPriceProvider(_priceProvider).getLpTokenPriceUsd();
		}
		uint8 stakingTokenDecimals = IERC20Detailed(stakingToken).decimals();
		uint256 usdValue = stakingTokenPrice.mul(amount).mul(10**18).div(10**8).div(10**stakingTokenDecimals);

		emit Staked(onBehalfOf, amount, true);
		emit Locked(onBehalfOf, usdValue, stakingToken != address(rdntToken));
	}

	function _insertLock(address _user, LockedBalance memory newLock) internal {
		LockedBalance[] storage locks = userLocks[_user];
		uint256 length = locks.length;
		uint256 i;
		while (i < length && locks[i].unlockTime < newLock.unlockTime) {
			i = i + 1;
		}
		locks.push(newLock);
		for (uint256 j = length; j > i; j -= 1) {
			locks[j] = locks[j - 1];
		}
		locks[i] = newLock;
	}

	/**
	 * @notice Add to earnings
	 * @dev Minted tokens receive rewards normally but incur a 50% penalty when
	 *  withdrawn before VEST_DURATION has passed.
	 */
	function mint(
		address user,
		uint256 amount,
		bool withPenalty
	) external override {
		require(minters[msg.sender]);
		if (amount == 0) return;

		_updateReward(user);

		if (user == address(this)) {
			// minting to this contract adds the new tokens as incentives for lockers
			_notifyReward(address(rdntToken), amount);
			return;
		}

		Balances storage bal = balances[user];
		bal.total = bal.total.add(amount);
		if (withPenalty) {
			bal.earned = bal.earned.add(amount);
			LockedBalance[] storage earnings = userEarnings[user];
			uint256 unlockTime = block.timestamp.add(VEST_DURATION);
			earnings.push(
				LockedBalance({amount: amount, unlockTime: unlockTime, multiplier: 1, duration: VEST_DURATION})
			);
		} else {
			bal.unlocked = bal.unlocked.add(amount);
		}
		emit Staked(user, amount, false);
	}

	/**
	 * @notice Withdraw tokens from earnings and unlocked.
	 * @dev First withdraws unlocked tokens, then earned tokens. Withdrawing earned tokens
	 *  incurs a 50% penalty which is distributed based on locked balances.
	 */
	function withdraw(uint256 amount) external {
		address _address = msg.sender;
		require(amount != 0);

		uint256 penaltyAmount;
		uint256 burnAmount;
		Balances storage bal = balances[_address];

		if (amount <= bal.unlocked) {
			bal.unlocked = bal.unlocked.sub(amount);
		} else {
			uint256 remaining = amount.sub(bal.unlocked);
			require(bal.earned >= remaining);
			bal.unlocked = 0;
			uint256 sumEarned = bal.earned;
			uint256 i;
			for (i = 0; ; i++) {
				uint256 earnedAmount = userEarnings[_address][i].amount;
				if (earnedAmount == 0) continue;
				(, uint256 penaltyFactor, , ) = _penaltyInfo(userEarnings[_address][i]);

				// Amount required from this lock, taking into account the penalty
				uint256 requiredAmount = remaining.mul(WHOLE).div(WHOLE.sub(penaltyFactor));
				if (requiredAmount >= earnedAmount) {
					requiredAmount = earnedAmount;
					remaining = remaining.sub(earnedAmount.mul(WHOLE.sub(penaltyFactor)).div(WHOLE)); // remaining -= earned * (1 - pentaltyFactor)
					if (remaining == 0) i++;
				} else {
					userEarnings[_address][i].amount = earnedAmount.sub(requiredAmount);
					remaining = 0;
				}
				sumEarned = sumEarned.sub(requiredAmount);

				penaltyAmount = penaltyAmount.add(requiredAmount.mul(penaltyFactor).div(WHOLE)); // penalty += amount * penaltyFactor
				burnAmount = burnAmount.add(penaltyAmount.mul(BURN).div(WHOLE)); // burn += penalty * burnFactor

				if (remaining == 0) {
					break;
				} else {
					require(sumEarned != 0);
				}
			}
			if (i > 0) {
				for (uint256 j = i; j < userEarnings[_address].length; j++) {
					userEarnings[_address][j - i] = userEarnings[_address][j];
				}
				for (uint256 j = 0; j < i; j++) {
					userEarnings[_address].pop();
				}
			}
			bal.earned = sumEarned;
		}

		// Update values
		bal.total = bal.total.sub(amount).sub(penaltyAmount);

		_withdrawTokens(_address, amount, penaltyAmount, burnAmount, false);
	}

	function ieeWithdrawableBalances(address user, uint256 unlockTime)
		internal
		view
		returns (
			uint256 amount,
			uint256 penaltyAmount,
			uint256 burnAmount,
			uint256 index
		)
	{
		for (uint256 i = 0; i < userEarnings[user].length; i++) {
			if (userEarnings[user][i].unlockTime == unlockTime) {
				(amount, , penaltyAmount, burnAmount) = _penaltyInfo(userEarnings[user][i]);
				index = i;
				break;
			}
		}
	}

	/**
	 * @notice Withdraw individual unlocked balance and earnings, optionally claim pending rewards.
	 */
	function individualEarlyExit(bool claimRewards, uint256 unlockTime) external {
		address onBehalfOf = msg.sender;
		require(unlockTime > block.timestamp);
		(uint256 amount, uint256 penaltyAmount, uint256 burnAmount, uint256 index) = ieeWithdrawableBalances(
			onBehalfOf,
			unlockTime
		);

		if (index >= userEarnings[onBehalfOf].length) {
			return;
		}

		for (uint256 i = index + 1; i < userEarnings[onBehalfOf].length; i++) {
			userEarnings[onBehalfOf][i - 1] = userEarnings[onBehalfOf][i];
		}
		userEarnings[onBehalfOf].pop();

		Balances storage bal = balances[onBehalfOf];
		bal.total = bal.total.sub(amount).sub(penaltyAmount);
		bal.earned = bal.earned.sub(amount).sub(penaltyAmount);

		_withdrawTokens(onBehalfOf, amount, penaltyAmount, burnAmount, claimRewards);
	}

	/**
	 * @notice Withdraw full unlocked balance and earnings, optionally claim pending rewards.
	 */
	function exit(bool claimRewards) external override {
		address onBehalfOf = msg.sender;
		(uint256 amount, uint256 penaltyAmount, uint256 burnAmount) = withdrawableBalance(onBehalfOf);

		delete userEarnings[onBehalfOf];

		Balances storage bal = balances[onBehalfOf];
		bal.total = bal.total.sub(bal.unlocked).sub(bal.earned);
		bal.unlocked = 0;
		bal.earned = 0;

		_withdrawTokens(onBehalfOf, amount, penaltyAmount, burnAmount, claimRewards);
	}

	/**
	 * @notice Claim all pending staking rewards.
	 */
	function getReward(address[] memory _rewardTokens) public {
		_updateReward(msg.sender);
		_getReward(msg.sender, _rewardTokens);
		lastClaimTime[msg.sender] = block.timestamp;
	}

	/**
	 * @notice Claim all pending staking rewards.
	 */
	function getAllRewards() external {
		return getReward(rewardTokens);
	}

	/**
	 * @notice Calculate earnings.
	 */
	function _earned(
		address _user,
		address _rewardToken,
		uint256 _balance,
		uint256 _currentRewardPerToken
	) internal view returns (uint256 earnings) {
		earnings = rewards[_user][_rewardToken];
		uint256 realRPT = _currentRewardPerToken.sub(userRewardPerTokenPaid[_user][_rewardToken]);
		earnings = earnings.add(_balance.mul(realRPT).div(1e18));
	}

	/**
	 * @notice Update user reward info.
	 */
	function _updateReward(address account) internal {
		uint256 balance = balances[account].lockedWithMultiplier;
		uint256 length = rewardTokens.length;
		for (uint256 i = 0; i < length; i++) {
			address token = rewardTokens[i];
			uint256 rpt = rewardPerToken(token);

			Reward storage r = rewardData[token];
			r.rewardPerTokenStored = rpt;
			r.lastUpdateTime = lastTimeRewardApplicable(token);

			if (account != address(this)) {
				rewards[account][token] = _earned(account, token, balance, rpt);
				userRewardPerTokenPaid[account][token] = rpt;
			}
		}
	}

	/**
	 * @notice Add new reward.
	 * @dev If prev reward period is not done, then it resets `rewardPerSecond` and restarts period
	 */
	function _notifyReward(address _rewardToken, uint256 reward) internal {
		Reward storage r = rewardData[_rewardToken];
		if (block.timestamp >= r.periodFinish) {
			r.rewardPerSecond = reward.mul(1e12).div(REWARDS_DURATION);
		} else {
			uint256 remaining = r.periodFinish.sub(block.timestamp);
			uint256 leftover = remaining.mul(r.rewardPerSecond).div(1e12);
			r.rewardPerSecond = reward.add(leftover).mul(1e12).div(REWARDS_DURATION);
		}

		r.lastUpdateTime = block.timestamp;
		r.periodFinish = block.timestamp.add(REWARDS_DURATION);
		r.balance = r.balance.add(reward);
	}

	/**
	 * @notice Notify unseen rewards.
	 * @dev for rewards other than stakingToken, every 24 hours we check if new
	 *  rewards were sent to the contract or accrued via aToken interest.
	 */
	function _notifyUnseenReward(address token) internal {
		require(token != address(0));
		if (token == address(rdntToken)) {
			return;
		}
		Reward storage r = rewardData[token];
		uint256 periodFinish = r.periodFinish;
		require(periodFinish != 0);
		if (periodFinish < block.timestamp.add(REWARDS_DURATION - REWARDS_LOOKBACK)) {
			uint256 unseen = IERC20(token).balanceOf(address(this)).sub(r.balance);
			if (unseen > 0) {
				_notifyReward(token, unseen);
			}
		}
	}

	/**
	 * @notice User gets reward
	 */
	function _getReward(address _user, address[] memory _rewardTokens) internal {
		middleFeeDistribution.forwardReward(_rewardTokens);
		uint256 length = _rewardTokens.length;
		for (uint256 i; i < length; i++) {
			address token = _rewardTokens[i];
			_notifyUnseenReward(token);
			uint256 reward = rewards[_user][token].div(1e12);
			if (reward > 0) {
				rewards[_user][token] = 0;
				rewardData[token].balance = rewardData[token].balance.sub(reward);

				IERC20(token).safeTransfer(_user, reward);

				emit RewardPaid(_user, token, reward);
			}
		}
	}

	/**
	 * @notice Withdraw tokens from MFD
	 */
	function _withdrawTokens(
		address onBehalfOf,
		uint256 amount,
		uint256 penaltyAmount,
		uint256 burnAmount,
		bool claimRewards
	) internal {
		require(onBehalfOf == msg.sender);
		_updateReward(onBehalfOf);

		rdntToken.safeTransfer(onBehalfOf, amount);
		if (penaltyAmount > 0) {
			if (burnAmount > 0) {
				rdntToken.safeTransfer(STARFLEET_TREASURY, burnAmount);
			}
			rdntToken.safeTransfer(daoTreasury, penaltyAmount.sub(burnAmount));
		}

		if (claimRewards) {
			_getReward(onBehalfOf, rewardTokens);
			lastClaimTime[onBehalfOf] = block.timestamp;
		}

		emit Withdrawn(onBehalfOf, address(rdntToken), amount, penaltyAmount, burnAmount);
	}

	/********************** Eligibility + Disqualification ***********************/

	/**
	 * @notice Withdraw all lockings tokens where the unlock time has passed
	 */
	function _cleanWithdrawableLocks(
		address user,
		uint256 totalLock,
		uint256 totalLockWithMultiplier,
		uint256 limit
	) internal returns (uint256 lockAmount, uint256 lockAmountWithMultiplier) {
		LockedBalance[] storage locks = userLocks[user];

		if (locks.length != 0) {
			uint256 length = locks.length <= limit ? locks.length : limit;
			for (uint256 i = 0; i < length; ) {
				if (locks[i].unlockTime <= block.timestamp) {
					lockAmount = lockAmount.add(locks[i].amount);
					lockAmountWithMultiplier = lockAmountWithMultiplier.add(locks[i].amount.mul(locks[i].multiplier));
					locks[i] = locks[locks.length - 1];
					locks.pop();
					length = length.sub(1);
				} else {
					i = i + 1;
				}
			}
			if (locks.length == 0) {
				lockAmount = totalLock;
				lockAmountWithMultiplier = totalLockWithMultiplier;
				delete userLocks[user];

				userlist.removeFromList(user);
			}
		}
	}

	/**
	 * @notice Withdraw all currently locked tokens where the unlock time has passed.
	 * @param _address of the user.
	 */
	function _withdrawExpiredLocksFor(
		address _address,
		bool isRelockAction,
		bool doTransfer,
		uint256 limit
	) internal returns (uint256 amount) {
		incentivesController.beforeLockUpdate(_address);
		_updateReward(_address);

		uint256 amountWithMultiplier;
		Balances storage bal = balances[_address];
		(amount, amountWithMultiplier) = _cleanWithdrawableLocks(_address, bal.locked, bal.lockedWithMultiplier, limit);
		bal.locked = bal.locked.sub(amount);
		bal.lockedWithMultiplier = bal.lockedWithMultiplier.sub(amountWithMultiplier);
		bal.total = bal.total.sub(amount);
		lockedSupply = lockedSupply.sub(amount);
		lockedSupplyWithMultiplier = lockedSupplyWithMultiplier.sub(amountWithMultiplier);
		incentivesController.afterLockUpdate(_address);

		if (!isRelockAction && !autoRelockDisabled[_address]) {
			_stake(amount, _address, defaultLockIndex[_address], true);
		} else {
			if (doTransfer) {
				IERC20(stakingToken).safeTransfer(_address, amount);
				emit Withdrawn(_address, stakingToken, amount, 0, 0);
			}
		}
		return amount;
	}

	/**
	 * @notice Withdraw all currently locked tokens where the unlock time has passed.
	 */
	function withdrawExpiredLocksFor(address _address) external override returns (uint256) {
		return _withdrawExpiredLocksFor(_address, false, true, userLocks[_address].length);
	}

	function withdrawExpiredLocksForWithOptions(
		address _address,
		uint256 _limit,
		bool _ignoreRelock
	) external returns (uint256) {
		if (_limit == 0) _limit = userLocks[_address].length;

		return _withdrawExpiredLocksFor(_address, _ignoreRelock, true, _limit);
	}

	function zapVestingToLp(address _user) external override returns (uint256 zapped) {
		require(msg.sender == lockZap);

		LockedBalance[] storage earnings = userEarnings[_user];
		uint256 length = earnings.length;

		for (uint256 i = 0; i < length; ) {
			// only vesting, so only look at currently locked items
			if (earnings[i].unlockTime > block.timestamp) {
				zapped = zapped.add(earnings[i].amount);
				// remove + shift array size
				earnings[i] = earnings[earnings.length - 1];
				earnings.pop();
				length = length.sub(1);
			} else {
				i = i.add(1);
			}
		}

		rdntToken.safeTransfer(lockZap, zapped);

		Balances storage bal = balances[_user];
		bal.earned = bal.earned.sub(zapped);
		bal.total = bal.total.sub(zapped);

		return zapped;
	}
}

