//    I n s t a n t
//
//        .:mmm.         .:mmm:.       .ii.  .:SSSSSSSSSSSSS.     .oOOOOOOOOOOOo.
//      .mMM'':Mm.     .:MM'':Mm:.     .II:  :SSs..........     .oOO'''''''''''OOo.
//    .:Mm'   ':Mm.   .:Mm'   'MM:.    .II:  'sSSSSSSSSSSSSS:.  :OO.           .OO:
//  .'mMm'     ':MM:.:MMm'     ':MM:.  .II:  .:...........:SS.  'OOo:.........:oOO'
//  'mMm'        ':MMmm'         'mMm:  II:  'sSSSSSSSSSSSSS'     'oOOOOOOOOOOOO'
//
//----------------------------------------------------------------------------------

import "@uniswap/v2-core/contracts/interfaces/IUniswapV2Pair.sol";
import "@uniswap/lib/contracts/libraries/TransferHelper.sol";
import "../../misc/interfaces/IWETH.sol";

import "../../dependencies/openzeppelin/contracts/IERC20.sol";
import "../../dependencies/openzeppelin/contracts/SafeMath.sol";
import "../../dependencies/uniswap/contracts/UniswapV2Library.sol";
import "../../dependencies/openzeppelin/contracts/SafeERC20.sol";
import "../../dependencies/openzeppelin/upgradeability/Initializable.sol";
import "../../dependencies/openzeppelin/upgradeability/OwnableUpgradeable.sol";

contract LiquidityZap is Initializable, OwnableUpgradeable {
    using SafeERC20 for IERC20;
    using SafeMath for uint256;

    address public _token;
    address public _tokenWETHPair;
    IWETH public _WETH;
    bool private initialized;

    function initialize() initializer external {
        __Ownable_init();
    }

    function initLiquidityZap(
        address token,
        address WETH,
        address tokenWethPair
    ) external {
        require(!initialized, "already initialized");
        _token = token;
        _WETH = IWETH(WETH);
        _tokenWETHPair = tokenWethPair;
        initialized = true;
    }

    fallback() external payable {
        if (msg.sender != address(_WETH)) {
            addLiquidityETHOnly(msg.sender);
        }
    }

    function zapETH(address payable _onBehalf)
        external
        payable
        returns (uint256 liquidity)
    {
        require(
            msg.value > 0,
            "LiquidityZAP: ETH amount must be greater than 0"
        );
        return addLiquidityETHOnly(_onBehalf);
    }

    function addLiquidityWETHOnly(uint256 _amount, address payable to)
        public
        returns (uint256 liquidity)
    {
        require(to != address(0), "LiquidityZAP: Invalid address");
        uint256 buyAmount = _amount.div(2);
        require(buyAmount > 0, "LiquidityZAP: Insufficient ETH amount");

        (uint256 reserveWeth, uint256 reserveTokens) = getPairReserves();
        uint256 outTokens = UniswapV2Library.getAmountOut(
            buyAmount,
            reserveWeth,
            reserveTokens
        );

        _WETH.transfer(_tokenWETHPair, buyAmount);

        (address token0, address token1) = UniswapV2Library.sortTokens(
            address(_WETH),
            _token
        );
        IUniswapV2Pair(_tokenWETHPair).swap(
            _token == token0 ? outTokens : 0,
            _token == token1 ? outTokens : 0,
            address(this),
            ""
        );

        return _addLiquidity(outTokens, buyAmount, to);
    }


    function addLiquidityETHOnly(address payable to)
        public
        payable
        returns (uint256 liquidity)
    {
        require(to != address(0), "LiquidityZAP: Invalid address");
        uint256 buyAmount = msg.value.div(2);
        require(buyAmount > 0, "LiquidityZAP: Insufficient ETH amount");
        _WETH.deposit{value: msg.value}();

        (uint256 reserveWeth, uint256 reserveTokens) = getPairReserves();
        uint256 outTokens = UniswapV2Library.getAmountOut(
            buyAmount,
            reserveWeth,
            reserveTokens
        );

        _WETH.transfer(_tokenWETHPair, buyAmount);

        (address token0, address token1) = UniswapV2Library.sortTokens(
            address(_WETH),
            _token
        );
        IUniswapV2Pair(_tokenWETHPair).swap(
            _token == token0 ? outTokens : 0,
            _token == token1 ? outTokens : 0,
            address(this),
            ""
        );

        return _addLiquidity(outTokens, buyAmount, to);
    }

    function quoteFromToken(uint256 tokenAmount)
        public
        view
        returns (uint256 optimalWETHAmount)
    {
        (uint256 wethReserve, uint256 tokenReserve) = getPairReserves();
        optimalWETHAmount = UniswapV2Library.quote(
            tokenAmount,
            tokenReserve,
            wethReserve
        );
    }

    function quote(uint256 wethAmount)
        public
        view
        returns (uint256 optimalTokenAmount)
    {
        (uint256 wethReserve, uint256 tokenReserve) = getPairReserves();
        optimalTokenAmount = UniswapV2Library.quote(
            wethAmount,
            wethReserve,
            tokenReserve
        );
    }

    // use with quote
    function standardAdd(uint256 tokenAmount, uint256 _wethAmt, address payable to)
        public
        returns (uint256 liquidity)
    {
        IERC20(_token).safeTransferFrom(msg.sender, address(this), tokenAmount);
        _WETH.transferFrom(msg.sender, address(this), _wethAmt);
        return _addLiquidity(tokenAmount, _wethAmt, to);
    }

    function _addLiquidity(
        uint256 tokenAmount,
        uint256 wethAmount,
        address payable to
    ) internal returns (uint256 liquidity) {
        (uint256 wethReserve, uint256 tokenReserve) = getPairReserves();

        uint256 optimalTokenAmount = UniswapV2Library.quote(
            wethAmount,
            wethReserve,
            tokenReserve
        );

        uint256 optimalWETHAmount;
        if (optimalTokenAmount > tokenAmount) {
            optimalWETHAmount = UniswapV2Library.quote(
                tokenAmount,
                tokenReserve,
                wethReserve
            );
            optimalTokenAmount = tokenAmount;
        } else optimalWETHAmount = wethAmount;

        assert(_WETH.transfer(_tokenWETHPair, optimalWETHAmount));
        IERC20(_token).safeTransfer(_tokenWETHPair, optimalTokenAmount);

        liquidity = IUniswapV2Pair(_tokenWETHPair).mint(to);

        //refund dust
        if (tokenAmount > optimalTokenAmount)
            IERC20(_token).safeTransfer(
                to,
                tokenAmount.sub(optimalTokenAmount)
            );
        if (wethAmount > optimalWETHAmount) {
            _WETH.transfer(
                to,
                wethAmount.sub(optimalWETHAmount)
            );
        }
    }

    function getLPTokenPerEthUnit(uint256 ethAmt)
        public
        view
        returns (uint256 liquidity)
    {
        (uint256 reserveWeth, uint256 reserveTokens) = getPairReserves();
        uint256 outTokens = UniswapV2Library.getAmountOut(
            ethAmt.div(2),
            reserveWeth,
            reserveTokens
        );
        uint256 _totalSupply = IUniswapV2Pair(_tokenWETHPair).totalSupply();

        (address token0, ) = UniswapV2Library.sortTokens(
            address(_WETH),
            _token
        );
        (uint256 amount0, uint256 amount1) = token0 == _token
            ? (outTokens, ethAmt.div(2))
            : (ethAmt.div(2), outTokens);
        (uint256 _reserve0, uint256 _reserve1) = token0 == _token
            ? (reserveTokens, reserveWeth)
            : (reserveWeth, reserveTokens);
        liquidity = SafeMath.min(
            amount0.mul(_totalSupply) / _reserve0,
            amount1.mul(_totalSupply) / _reserve1
        );
    }

    function getPairReserves()
        internal
        view
        returns (uint256 wethReserves, uint256 tokenReserves)
    {
        (address token0, ) = UniswapV2Library.sortTokens(
            address(_WETH),
            _token
        );
        (uint256 reserve0, uint256 reserve1, ) = IUniswapV2Pair(_tokenWETHPair)
            .getReserves();
        (wethReserves, tokenReserves) = token0 == _token
            ? (reserve1, reserve0)
            : (reserve0, reserve1);
    }
}

contracts/zap/helpers/UniswapPoolHelper..content:
// SPDX-License-Identifier: agpl-3.0
pragma solidity 0.7.6;
pragma abicoder v2;

import "./DustRefunder.sol";

import "@uniswap/v2-core/contracts/interfaces/IUniswapV2Factory.sol";
import "@uniswap/v2-core/contracts/interfaces/IUniswapV2Pair.sol";
import "../../dependencies/uniswap/contracts/UniswapV2Library.sol";

import "../../dependencies/openzeppelin/contracts/IERC20.sol";
import "../../dependencies/openzeppelin/contracts/SafeERC20.sol";
import "../../dependencies/openzeppelin/contracts/SafeMath.sol";
import "../../dependencies/openzeppelin/upgradeability/Initializable.sol";
import "../../dependencies/openzeppelin/upgradeability/OwnableUpgradeable.sol";
import "../../dependencies/uniswap/contracts/UniswapV2Library.sol";
import "../../dependencies/math/HomoraMath.sol";

import "../../interfaces/uniswap/IUniswapV2Router02.sol";
import "../../interfaces/ILiquidityZap.sol";
import "../../interfaces/IMultiFeeDistribution.sol";
import "../../misc/interfaces/IWETH.sol";
import "../../interfaces/ILendingPool.sol";
import "../../interfaces/IPoolHelper.sol";

contract UniswapPoolHelper is Initializable, OwnableUpgradeable, DustRefunder {
    using SafeERC20 for IERC20;
    using SafeMath for uint256;
    using HomoraMath for uint;


    address public lpTokenAddr;
    address public rdntAddr;
    address public wethAddr;

    IUniswapV2Router02 public router;
    ILiquidityZap public liquidityZap;

    function initialize(
        address _rdntAddr,
        address _wethAddr,
        address _routerAddr,
        ILiquidityZap _liquidityZap
    ) external initializer {
        __Ownable_init();

        rdntAddr = _rdntAddr;
        wethAddr = _wethAddr;

        router = IUniswapV2Router02(_routerAddr);
        liquidityZap = _liquidityZap;
    }

    receive() external payable {}

    function initializePool() public {
        lpTokenAddr = IUniswapV2Factory(router.factory()).createPair(
            rdntAddr,
            wethAddr
        );

        IERC20 rdnt = IERC20(rdntAddr);
        rdnt.safeApprove(address(router), type(uint256).max);
        rdnt.safeApprove(address(liquidityZap), type(uint256).max);
        IERC20(wethAddr).approve(address(liquidityZap), type(uint256).max);
        IERC20(wethAddr).approve(address(router), type(uint256).max);

        router.addLiquidity(
            address(rdnt),
            wethAddr,
            rdnt.balanceOf(address(this)),
            IERC20(wethAddr).balanceOf(address(this)),
            0,
            0,
            address(this),
            block.timestamp
        );

        IERC20 lp = IERC20(lpTokenAddr);
        lp.safeTransfer(msg.sender, lp.balanceOf(address(this)));

        liquidityZap.initLiquidityZap(rdntAddr, wethAddr, lpTokenAddr);
    }

    function zapWETH(uint256 amount)
        public
        returns (uint256 liquidity)
    {
        IWETH WETH = IWETH(wethAddr);
        WETH.transferFrom(msg.sender, address(liquidityZap), amount);
        liquidity = liquidityZap.addLiquidityWETHOnly(amount, address(this));
        IERC20 lp = IERC20(lpTokenAddr);

        liquidity = lp.balanceOf(address(this));
        lp.safeTransfer(msg.sender, liquidity);
        refundDust(rdntAddr, wethAddr, msg.sender);
    }

    function getReserves() public view returns (
        uint256 rdnt,
        uint256 weth,
        uint256 lpTokenSupply
    ) {
        IUniswapV2Pair lpToken = IUniswapV2Pair(lpTokenAddr);

        (uint256 reserve0, uint256 reserve1, ) = lpToken.getReserves();
        weth = lpToken.token0() != address(rdntAddr)
            ? reserve0
            : reserve1;
        rdnt = lpToken.token0() == address(rdntAddr)
            ? reserve0
            : reserve1;

        lpTokenSupply = lpToken.totalSupply();
    }

    // UniV2 / SLP LP Token Price
    // Alpha Homora Fair LP Pricing Method (flash loan resistant)
    // https://cmichel.io/pricing-lp-tokens/
    // https://blog.alphafinance.io/fair-lp-token-pricing/
    // https://github.com/AlphaFinanceLab/alpha-homora-v2-contract/blob/master/contracts/oracle/UniswapV2Oracle.sol
    function getLpPrice(uint rdntPriceInEth) public view returns (uint256 priceInEth) {
        IUniswapV2Pair lpToken = IUniswapV2Pair(lpTokenAddr);

        (uint reserve0, uint reserve1, ) = lpToken.getReserves();
        uint wethReserve = lpToken.token0() != address(rdntAddr)
            ? reserve0
            : reserve1;
        uint rdntReserve = lpToken.token0() == address(rdntAddr)
            ? reserve0
            : reserve1;

        uint lpSupply = lpToken.totalSupply();

        uint sqrtK = HomoraMath.sqrt(rdntReserve.mul(wethReserve)).fdiv(lpSupply); // in 2**112

        // rdnt in eth, decis 8
        uint px0 = rdntPriceInEth.mul(2 ** 112); // in 2**112
        // eth in eth, decis 8
        uint px1 = uint256(100000000).mul(2 ** 112); // in 2**112

        // fair token0 amt: sqrtK * sqrt(px1/px0)
        // fair token1 amt: sqrtK * sqrt(px0/px1)
        // fair lp price = 2 * sqrt(px0 * px1)
        // split into 2 sqrts multiplication to prevent uint overflow (note the 2**112)
        uint result = sqrtK.mul(2).mul(HomoraMath.sqrt(px0)).div(2**56).mul(HomoraMath.sqrt(px1)).div(2**56);
        priceInEth = result.div(2 ** 112);
    }

    function zapTokens(uint256 _wethAmt, uint256 _rdntAmt)
        public
        returns (uint256 liquidity)
    {
        IWETH WETH = IWETH(wethAddr);
        WETH.transferFrom(msg.sender, address(this), _wethAmt);
        IERC20(rdntAddr).safeTransferFrom(msg.sender, address(this), _rdntAmt);
        liquidity = liquidityZap.standardAdd(
            _rdntAmt,
            _wethAmt,
            address(this)
        );
        IERC20 lp = IERC20(lpTokenAddr);
        liquidity = lp.balanceOf(address(this));
        lp.safeTransfer(msg.sender, liquidity);
        refundDust(rdntAddr, wethAddr, msg.sender);
    }

    function quoteFromToken(uint256 tokenAmount)
        public
        view
        returns (uint256 optimalWETHAmount)
    {
        return liquidityZap.quoteFromToken(tokenAmount);
    }

    function getLiquidityZap() public view returns (address) {
        return address(liquidityZap);
    }

    function setLiquidityZap (address _liquidityZap) external onlyOwner {
        liquidityZap = ILiquidityZap(_liquidityZap);
    }
}

contracts/zap/LockZap.sol.content:
// SPDX-License-Identifier: agpl-3.0
pragma solidity 0.7.6;
pragma abicoder v2;

import "./helpers/DustRefunder.sol";

import "@uniswap/v2-core/contracts/interfaces/IUniswapV2Factory.sol";

import "../dependencies/openzeppelin/contracts/IERC20.sol";
import "../dependencies/openzeppelin/contracts/ERC20.sol";
import "../dependencies/openzeppelin/contracts/SafeERC20.sol";
import "../dependencies/openzeppelin/contracts/SafeMath.sol";
import "../dependencies/openzeppelin/upgradeability/Initializable.sol";
import "../dependencies/openzeppelin/upgradeability/OwnableUpgradeable.sol";
import "../dependencies/openzeppelin/upgradeability/PausableUpgradeable.sol";

import "../interfaces/uniswap/IUniswapV2Router02.sol";
import "../interfaces/ILiquidityZap.sol";
import "../interfaces/IMultiFeeDistribution.sol";
import "../interfaces/ILendingPool.sol";
import "../interfaces/IPoolHelper.sol";
import "../interfaces/IPriceProvider.sol";
import "../interfaces/IChainlinkAggregator.sol";
import "../misc/interfaces/IWETH.sol";

/// @title Borrow gate via stargate
/// @author Radiant
/// @dev All function calls are currently implemented without side effects
contract LockZap is Initializable, OwnableUpgradeable, PausableUpgradeable, DustRefunder {
    using SafeERC20 for IERC20;
    using SafeMath for uint256;

    /// @notice RAITO Divisor
    uint256 public constant RATIO_DIVISOR = 10000;

    /// @notice Acceptable ratio
    uint256 public constant ACCEPTABLE_RATIO = 9500;

    /// @notice Wrapped ETH
    IWETH public WETH;

    /// @notice RDNT token address
    address public rdntAddr;

    /// @notice LP Fee distribution contract
    IMultiFeeDistribution public lpMfd;

    /// @notice Multi Fee distribution contract
    IMultiFeeDistribution public mfd;

    /// @notice Lending Pool contract
    ILendingPool public lendingPool;

    /// @notice Pool helper contract
    IPoolHelper public poolHelper;

    /// @notice Price provider contract
    IPriceProvider public priceProvider;

    /// @notice ETH oracle contract
    IChainlinkAggregator public ethOracle;

    /// @notice Emitted when zap is done
    event Zapped(
        bool _borrow,
        uint256 _ethAmt,
        uint256 _rdntAmt,
        address indexed _from,
        address indexed _onBehalf,
        uint256 _lockTypeIndex
    );

    uint256 public ETH_LP_RATIO; // paramter to set the ratio of ETH in the LP token, can be 2000 for an 80/20 bal lp


    /**
     * @notice Initializer
     * @param _poolHelper Pool helper address
     * @param _lendingPool Lending pool
     * @param _weth WETH address
     * @param _rdntAddr RDNT token address
     */
    function initialize(
        IPoolHelper _poolHelper,
        ILendingPool _lendingPool,
        IWETH _weth,
        address _rdntAddr
    ) external initializer {
        __Ownable_init();
        __Pausable_init();

        lendingPool = _lendingPool;
        poolHelper = _poolHelper;
        WETH = _weth;
        rdntAddr = _rdntAddr;
        ETH_LP_RATIO = 5000;
    }

    receive() external payable {}

    /**
     * @notice Set Price Provider.
     * @param _provider Price provider contract address.
     */
    function setPriceProvider(address _provider) external onlyOwner {
        priceProvider = IPriceProvider(_provider);
        ethOracle = IChainlinkAggregator(priceProvider.baseTokenPriceInUsdProxyAggregator());
    }

    /**
     * @notice Set LP fee distribution contract.
     * @param _lpMfdAddr New contract address.
     */
    function setLpMfd(address _lpMfdAddr) external onlyOwner {
        lpMfd = IMultiFeeDistribution(_lpMfdAddr);
    }

    /**
     * @notice Set Multi fee distribution contract.
     * @param _mfdAddr New contract address.
     */
    function setMfd(address _mfdAddr) external onlyOwner {
        mfd = IMultiFeeDistribution(_mfdAddr);
    }

    /**
     * @notice Set Pool Helper contract
     * @param _poolHelper New PoolHelper contract address.
     */
    function setPoolHelper (address _poolHelper) external onlyOwner {
        poolHelper = IPoolHelper(_poolHelper);
    }

    /**
     * @notice Returns pool helper address
     */
    function getPoolHelper() public view returns (address) {
        return address(poolHelper);
    }

    /**
     * @notice Get Variable debt token address
     * @param _asset underlying.
     */
    function getVDebtToken(address _asset) public view returns (address) {
        DataTypes.ReserveData memory reserveData = lendingPool.getReserveData(
            _asset
        );
        return reserveData.variableDebtTokenAddress;
    }

    /**
     * @notice Get quote from the pool
     * @param _tokenAmount amount of tokens.
     */
    function quoteFromToken(uint256 _tokenAmount) public view returns (uint256 optimalWETHAmount) {
        return poolHelper.quoteFromToken(_tokenAmount);
    }

    /**
     * @notice Zap tokens to stake LP
     * @param _borrow option to borrow ETH
     * @param _wethAmt amount of WETH.
     * @param _rdntAmt amount of RDNT.
     * @param _lockTypeIndex lock length index.
     */
    function zap(
        bool _borrow,
        uint256 _wethAmt,
        uint256 _rdntAmt,
        uint256 _lockTypeIndex
    ) public payable returns (uint256 liquidity) {
        return _zap(_borrow, _wethAmt, _rdntAmt, msg.sender, msg.sender, _lockTypeIndex, msg.sender);
    }

    /**
     * @notice Zap tokens to stake LP
     * @dev It will use default lock index
     * @param _borrow option to borrow ETH
     * @param _wethAmt amount of WETH.
     * @param _rdntAmt amount of RDNT.
     * @param _onBehalf user address to be zapped.
     */
    function zapOnBehalf(
        bool _borrow,
        uint256 _wethAmt,
        uint256 _rdntAmt,
        address _onBehalf
    ) public payable returns (uint256 liquidity) {
        uint256 duration = lpMfd.defaultLockIndex(_onBehalf);
        return _zap(_borrow, _wethAmt, _rdntAmt, msg.sender, _onBehalf, duration, _onBehalf);
    }

    /**
     * @notice Zap tokens from vesting
     * @param _borrow option to borrow ETH
     * @param _lockTypeIndex lock length index.
     */
    function zapFromVesting(bool _borrow, uint256 _lockTypeIndex) public payable returns (uint256 liquidity) {
        uint256 rdntAmt = mfd.zapVestingToLp(msg.sender);
        uint256 wethAmt = quoteFromToken(rdntAmt);
        return _zap(_borrow, wethAmt, rdntAmt, address(this), msg.sender, _lockTypeIndex, msg.sender);
    }

    /**
     * @notice Borrow ETH
     * @param _amount of ETH
     */
    function _executeBorrow(uint256 _amount) internal {
        (, , uint256 availableBorrowsETH, , , ) = lendingPool
            .getUserAccountData(msg.sender);
        uint256 amountInETH = _amount.mul(10**8).div(
            10**ERC20(address(WETH)).decimals()
        );
        require(
            availableBorrowsETH > amountInETH,
            "Not enough availableBorrowsETH"
        );

        uint16 referralCode = 0;
        lendingPool.borrow(
            address(WETH),
            _amount,
            2,
            referralCode,
            msg.sender
        );
    }

    /**
     * @notice Calculates slippage ratio from WETH to LP
     * @param _ethAmt ETH amount
     * @param _liquidity LP token amount
     */
    function _calcSlippage(uint256 _ethAmt, uint256 _liquidity) internal returns (uint256 ratio) {
        priceProvider.update();
        uint256 priceWETHamount = _ethAmt.mul(uint256(ethOracle.latestAnswer())).div(1E18);
        uint256 priceLPamount = _liquidity * priceProvider.getLpTokenPriceUsd();
        ratio = priceLPamount.mul(RATIO_DIVISOR).div(priceWETHamount);
        ratio = ratio.div(1E18);
    }

    /**
     * @notice Zap into LP
     * @param _borrow option to borrow ETH
     * @param _wethAmt amount of WETH.
     * @param _rdntAmt amount of RDNT.
     * @param _from src address of RDNT
     * @param _onBehalf of the user.
     * @param _lockTypeIndex lock length index.
     * @param _refundAddress dust is refunded to this address.
     */
    function _zap(
        bool _borrow,
        uint256 _wethAmt,
        uint256 _rdntAmt,
        address _from,
        address _onBehalf,
        uint256 _lockTypeIndex,
        address _refundAddress
    ) internal returns (uint256 liquidity) {
        require(
            _wethAmt != 0 ||
            msg.value != 0
        , "ETH required");

        if(msg.value != 0) {
            require(!_borrow, "invalid zap ETH source");
            _wethAmt = msg.value;
            WETH.deposit{value: _wethAmt}();
        } else {
            if (_borrow) {
                _executeBorrow(_wethAmt);
            } else {
                WETH.transferFrom(msg.sender, address(this), _wethAmt);
            }
        }

        WETH.approve(address(poolHelper), _wethAmt);
        //case where rdnt is matched with borrowed ETH
        if(_rdntAmt != 0) {
            require(_wethAmt >= quoteFromToken(_rdntAmt), "ETH sent is not enough");

            // _from == this when zapping from vesting
            if (_from != address(this)) {
                IERC20(rdntAddr).safeTransferFrom(msg.sender, address(this), _rdntAmt);
            }

            IERC20(rdntAddr).safeApprove(address(poolHelper), _rdntAmt);
            liquidity = poolHelper.zapTokens(_wethAmt, _rdntAmt);
            if(address(priceProvider) != address(0)){
                uint256 slippage = _calcSlippage(_wethAmt.mul(RATIO_DIVISOR).div(ETH_LP_RATIO), liquidity);
                require(slippage >= ACCEPTABLE_RATIO, "too much slippage");
            }
        } else {
            liquidity = poolHelper.zapWETH(_wethAmt);
            if(address(priceProvider) != address(0)){
                uint256 slippage = _calcSlippage(_wethAmt, liquidity);
                require(slippage >= ACCEPTABLE_RATIO, "too much slippage");
            }
        }

        IERC20(poolHelper.lpTokenAddr()).safeApprove(
            address(lpMfd),
            liquidity
        );
        lpMfd.stake(liquidity, _onBehalf, _lockTypeIndex);
        emit Zapped(_borrow, _wethAmt, _rdntAmt, _from, _onBehalf, _lockTypeIndex);

        refundDust(rdntAddr, address(WETH), _refundAddress);
    }


}
